// Extract and process SignSnap Home data for HubSpot
const items = $input.all();
const guestData = items[0].json.body;

// Extract core contact information
const email = guestData.email || null;
const firstName = guestData.first_name || '';
const lastName = guestData.last_name || '';
const phone = guestData.phone_number || null;

// Extract open house details
const propertyAddress = guestData.openHouseTitle || '';
const visitDate = guestData.submissionTimestamp || new Date().toISOString();
const hasAgent = guestData.are_you_currently_working_with_an_agent || 'Not specified';

// Optional fields (may not exist in all forms)
const rating = guestData.what_did_you_rate_the_house || null;
const buyerAgreement = guestData.do_you_have_a_signed_buyer_agreement || null;

// Separate standard fields from custom fields
const standardFields = [
  'openHouseTitle', 'openHouseType', 'submissionTimestamp',
  'guestPhotoUrl', 'first_name', 'last_name', 'phone_number',
  'email', 'are_you_currently_working_with_an_agent',
  'what_did_you_rate_the_house', 'do_you_have_a_signed_buyer_agreement',
  'webhookUrl', 'executionMode'
];

const customFields = {};
for (const [key, value] of Object.entries(guestData)) {
  if (!standardFields.includes(key) && value !== null && value !== undefined && value !== '') {
    customFields[key] = value;
  }
}

// Build custom fields text for notes
const customFieldsText = Object.entries(customFields)
  .map(([key, value]) => {
    const formattedKey = key.replace(/_/g, ' ')
      .split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
    return `${formattedKey}: ${value}`;
  })
  .join('\n');

// Calculate lead score (0-100)
let leadScore = 50; // Base score

if (hasAgent === 'No' || hasAgent === 'no') {
  leadScore += 30; // Hot lead - no agent
}

if (rating) {
  const ratingNum = parseInt(rating);
  if (ratingNum >= 4) leadScore += 20; // High rating
  else if (ratingNum <= 2) leadScore -= 20; // Low rating
}

if (buyerAgreement === 'No' || buyerAgreement === 'no') {
  leadScore += 10; // No agreement = more available
}

leadScore = Math.max(0, Math.min(100, leadScore)); // Clamp 0-100

// Determine lead status
let leadStatus = 'OPEN';
if (leadScore >= 70) leadStatus = 'HOT';
else if (leadScore >= 50) leadStatus = 'WARM';
else if (leadScore < 40) leadStatus = 'COLD';

// Build comprehensive notes for HubSpot
const notes = `ðŸ  Open House Visit\n\nProperty: ${propertyAddress}\nVisit Date: ${new Date(visitDate).toLocaleString('en-US', { dateStyle: 'full', timeStyle: 'short' })}\nHas Agent: ${hasAgent}${rating ? `\nProperty Rating: ${rating}/5` : ''}${buyerAgreement ? `\nBuyer Agreement: ${buyerAgreement}` : ''}\n\nLead Score: ${leadScore}/100\nLead Status: ${leadStatus}${customFieldsText ? `\n\n--- Additional Information ---\n${customFieldsText}` : ''}\n\nðŸ“ Source: SignSnap Home Open House Sign-In`;

return [{
  json: {
    // HubSpot contact fields
    email: email,
    firstname: firstName,
    lastname: lastName,
    phone: phone,

    // Property & visit details
    propertyAddress: propertyAddress,
    visitDate: visitDate,
    hasAgent: hasAgent,
    rating: rating,

    // Scoring
    leadScore: leadScore,
    leadStatus: leadStatus,

    // Notes to add to contact
    notes: notes,

    // Lifecycle stage
    lifecyclestage: 'lead',

    // Original data for reference
    _raw: guestData
  }
}];
