parameters.jsCode:
// Extract and format client data from GHL opportunity
const opportunity = $input.item.json;
const contact = opportunity.contact || {};
const relations = opportunity.relations || [];

// Get primary contact relation data as fallback
const primaryRelation = relations.find(r => r.primary === true) || {};

// Extract client information with fallbacks
const clientName = contact.name || primaryRelation.contactName || primaryRelation.fullName || 'Unknown Client';
const clientEmail = contact.email || primaryRelation.email || '';
const clientPhone = contact.phone || primaryRelation.phone || '';
const companyName = contact.companyName || primaryRelation.companyName || '';

// Format date for folder name (YYYY-MM-DD)
const today = new Date().toISOString().split('T')[0];

// Create safe folder name (remove special characters)
const safeFolderName = clientName.replace(/[^a-zA-Z0-9\s-]/g, '').trim();

return {
  // Core client info
  clientName: clientName,
  clientEmail: clientEmail,
  clientPhone: clientPhone,
  companyName: companyName,

  // Deal information
  dealId: opportunity.id || '',
  dealName: opportunity.name || clientName,
  dealValue: opportunity.monetaryValue || 0,
  dealStatus: opportunity.status || 'won',

  // Pipeline info
  pipelineId: opportunity.pipelineId || '',
  pipelineStageId: opportunity.pipelineStageId || '',

  // Contact & Location IDs
  contactId: opportunity.contactId || contact.id || '',
  locationId: opportunity.locationId || '',
  assignedTo: opportunity.assignedTo || '',

  // Folder naming for Google Drive
  folderName: `${safeFolderName}_${today}`,

  // Document naming templates
  contractName: `${safeFolderName} - Service Agreement`,
  kickoffDeckName: `${safeFolderName} - Onboarding Deck`,

  // Timestamps
  createdAt: opportunity.createdAt || '',
  updatedAt: opportunity.updatedAt || '',
  lastStageChangeAt: opportunity.lastStageChangeAt || '',
  timestamp: new Date().toISOString(),

  // Additional useful data
  tags: contact.tags || [],
  source: opportunity.source || 'manual',

  // Validation flags
  hasEmail: !!(clientEmail && clientEmail.length > 0),
  hasPhone: !!(clientPhone && clientPhone.length > 0),
  isValid: !!(clientName && clientName !== 'Unknown Client')
};

