// OpenAI output comes as string in $json.text
const raw = $json["text"];

// Parse safely
let parsed;
try {
  parsed = JSON.parse(raw);
} catch (e) {
  throw new Error("Invalid JSON returned by OpenAI: " + raw);
}

return parsed;

// Input = parsed JSON from AI
const data = $json;

// Weights (can be adjusted or moved to Config sheet later)
const weights = {
  specificity: 0.35,
  structure: 0.15,
  bias_free_language: 0.15,
  actionability: 0.10,
  depth: 0.25,
};

// ‚úÖ Fallback for structure (AI might send structure or structure_star)
const structureValue = data.structure ?? data.structure_star ?? 0;

// Calculate weighted score safely
let total = (
  (data.specificity * weights.specificity) +
  (structureValue * weights.structure) +
  (data.bias_free_language * weights.bias_free_language) +
  (data.actionability * weights.actionability) +
  (data.depth * weights.depth)
) / (
  weights.specificity +
  weights.structure +
  weights.bias_free_language +
  weights.actionability +
  weights.depth
);

// Scale to 0‚Äì100
total = Math.round(total * 20);

// Flags
const flags = [];
if ((data.specificity ?? 0) < 3 || (data.depth ?? 0) < 3) {
  flags.push("low_detail");
}
if ((data.bias_free_language ?? 0) < 3) {
  flags.push("bias");
}

// Format vague phrases if they exist
let vagueFormatted = "";
if (Array.isArray(data.vague_phrases) && data.vague_phrases.length > 0) {
  vagueFormatted = data.vague_phrases.map(p => `‚Ä¢ ${p}`).join("\n");
}

// Return clean JSON
return {
  json: {
    Score: total,
    Flags: flags.join(", "),
    LLM_JSON: JSON.stringify(data),
    VaguePhrasesFormatted: vagueFormatted,   // for Slack message
    row_number: $json.row_number,            // keep tracking the row
    Role: $item(0).$node["Fetch Raw Feedback Data"].json.Role,
    Stage: $item(0).$node["Fetch Raw Feedback Data"].json.Stage
  }
};

