// Get the input data
const items = $input.all();

// Process each item
const processedItems = items.map(item => {
  const data = item.json;

  // Clean the transcript
  let cleanedTranscript = data.rawTranscript || '';

  // Remove multiple spaces
  cleanedTranscript = cleanedTranscript.replace(/\s+/g, ' ');

  // Remove common timestamp patterns (e.g., [00:01:23] or (00:01:23))
  cleanedTranscript = cleanedTranscript.replace(/[\[\(]\d{2}:\d{2}:\d{2}[\]\)]/g, '');

  // Remove speaker labels if formatted as "Speaker 1:", "Speaker 2:", etc.
  cleanedTranscript = cleanedTranscript.replace(/Speaker \d+:/g, '');

  // Trim whitespace
  cleanedTranscript = cleanedTranscript.trim();

  // Count words for context
  const wordCount = cleanedTranscript.split(/\s+/).length;

  // Estimate reading time (average 200 words per minute)
  const estimatedMinutes = Math.ceil(wordCount / 200);

  return {
    json: {
      ...data,
      cleanedTranscript: cleanedTranscript,
      transcriptWordCount: wordCount,
      estimatedReadingTime: `${estimatedMinutes} minutes`,
      processingTimestamp: new Date().toISOString()
    }
  };
});

return processedItems;

