parameters.jsCode:
// Get ALL items from Merge (Append gives us an array of items)
const items = $input.all();

console.log('=== STARTING PARSE ===');
console.log('Total items:', items.length);

// Get metadata from earlier nodes
let codeData = {};
try {
  codeData = $('Clean Transcript').first().json;
  console.log('‚úì Got metadata from Code node');
} catch (error) {
  console.log('‚úó Could not access Code node:', error.message);
}

// Initialize with metadata
let meetingData = {
  meetingTitle: codeData.meetingTitle || 'Untitled Meeting',
  meetingDate: codeData.meetingDate || new Date().toISOString().split('T')[0],
  participants: codeData.participants || [],
  duration: codeData.duration || 'Not specified',
  rawTranscript: codeData.rawTranscript || '',
  cleanedTranscript: codeData.cleanedTranscript || '',
  transcriptWordCount: codeData.transcriptWordCount || 0,
  estimatedReadingTime: codeData.estimatedReadingTime || 'Unknown',
  processingTimestamp: codeData.processingTimestamp || new Date().toISOString(),
  summary: '',
  actionItems: []
};

console.log('\n=== PROCESSING', items.length, 'OPENAI RESPONSES ===');

// Process each item from Merge
items.forEach((item, index) => {
  console.log(`\nItem ${index}:`);

  const data = item.json;

  // OpenAI node returns: { message: { role, content } }
  if (data.message && data.message.content) {
    const content = data.message.content;
    console.log('  Content length:', content.length);
    console.log('  Preview:', content.substring(0, 80));

    // Check if action items JSON
    if (content.includes('"action_items"')) {
      console.log('  ‚Üí ACTION ITEMS');
      try {
        const parsed = JSON.parse(content);
        meetingData.actionItems = parsed.action_items || [];
        console.log('  ‚úì Parsed:', meetingData.actionItems.length, 'items');
      } catch (error) {
        console.error('  ‚úó Parse error:', error.message);
      }
    } else {
      console.log('  ‚Üí SUMMARY');
      meetingData.summary = content.trim();
      console.log('  ‚úì Length:', meetingData.summary.length);
    }
  }
});

console.log('\n=== RESULTS ===');
console.log('Summary:', meetingData.summary ? `${meetingData.summary.length} chars` : 'EMPTY');
console.log('Action Items:', meetingData.actionItems.length);

// Enrich action items
meetingData.actionItems = meetingData.actionItems.map((item, index) => {
  let formattedDeadline = 'TBD';
  let daysUntil = null;
  let deadlineStatus = 'no-deadline';

  if (item.deadline && item.deadline !== 'null' && item.deadline !== null) {
    try {
      const deadlineDate = new Date(item.deadline);
      if (!isNaN(deadlineDate.getTime())) {
        formattedDeadline = deadlineDate.toISOString().split('T')[0];
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        deadlineDate.setHours(0, 0, 0, 0);
        daysUntil = Math.ceil((deadlineDate - today) / (1000 * 60 * 60 * 24));

        if (daysUntil < 0) deadlineStatus = 'overdue';
        else if (daysUntil === 0) deadlineStatus = 'today';
        else if (daysUntil <= 3) deadlineStatus = 'urgent';
        else if (daysUntil <= 7) deadlineStatus = 'upcoming';
        else deadlineStatus = 'future';
      }
    } catch (e) {}
  }

  return {
    id: `AI-${Date.now()}-${index}`,
    number: index + 1,
    task: item.task || 'No task description',
    assignee: item.assignee || 'Unassigned',
    deadline: formattedDeadline,
    deadlineOriginal: item.deadline,
    daysUntilDeadline: daysUntil,
    deadlineStatus: deadlineStatus,
    priority: (item.priority || 'normal').toLowerCase(),
    context: item.context || '',
    status: 'pending',
    createdAt: new Date().toISOString(),
    meetingReference: meetingData.meetingTitle,
    meetingDate: meetingData.meetingDate
  };
});

// Sort
const priorityOrder = { urgent: 0, high: 1, normal: 2, low: 3 };
const statusOrder = { overdue: 0, today: 1, urgent: 2, upcoming: 3, future: 4, 'no-deadline': 5 };

meetingData.actionItems.sort((a, b) => {
  const priorityDiff = priorityOrder[a.priority] - priorityOrder[b.priority];
  if (priorityDiff !== 0) return priorityDiff;
  const statusDiff = statusOrder[a.deadlineStatus] - statusOrder[b.deadlineStatus];
  if (statusDiff !== 0) return statusDiff;
  if (a.deadline === 'TBD' && b.deadline !== 'TBD') return 1;
  if (a.deadline !== 'TBD' && b.deadline === 'TBD') return -1;
  if (a.deadline !== 'TBD' && b.deadline !== 'TBD') {
    return new Date(a.deadline) - new Date(b.deadline);
  }
  return 0;
});

meetingData.actionItems = meetingData.actionItems.map((item, index) => ({
  ...item,
  number: index + 1
}));

// Generate metadata
const urgentCount = meetingData.actionItems.filter(i => i.priority === 'urgent').length;
const highCount = meetingData.actionItems.filter(i => i.priority === 'high').length;
const normalCount = meetingData.actionItems.filter(i => i.priority === 'normal').length;
const lowCount = meetingData.actionItems.filter(i => i.priority === 'low').length;
const assigneeBreakdown = meetingData.actionItems.reduce((acc, item) => {
  acc[item.assignee] = (acc[item.assignee] || 0) + 1;
  return acc;
}, {});
const overdueCount = meetingData.actionItems.filter(i => i.deadlineStatus === 'overdue').length;
const todayCount = meetingData.actionItems.filter(i => i.deadlineStatus === 'today').length;
const urgentDeadlineCount = meetingData.actionItems.filter(i => i.deadlineStatus === 'urgent').length;
const upcomingCount = meetingData.actionItems.filter(i => i.deadlineStatus === 'upcoming').length;

meetingData.summaryMetadata = {
  generatedAt: new Date().toISOString(),
  actionItemCount: meetingData.actionItems.length,
  participantCount: meetingData.participants.length,
  hasUrgentItems: urgentCount > 0,
  hasHighPriorityItems: highCount > 0,
  hasUnassignedItems: meetingData.actionItems.some(i => i.assignee === 'Unassigned'),
  hasOverdueItems: overdueCount > 0,
  hasTodayItems: todayCount > 0,
  priorityBreakdown: { urgent: urgentCount, high: highCount, normal: normalCount, low: lowCount },
  assigneeBreakdown: assigneeBreakdown,
  itemsWithDeadlines: meetingData.actionItems.filter(i => i.deadline !== 'TBD').length,
  itemsWithoutDeadlines: meetingData.actionItems.filter(i => i.deadline === 'TBD').length,
  deadlineStatus: {
    overdue: overdueCount,
    today: todayCount,
    urgent: urgentDeadlineCount,
    upcoming: upcomingCount,
    future: meetingData.actionItems.filter(i => i.deadlineStatus === 'future').length,
    noDeadline: meetingData.actionItems.filter(i => i.deadlineStatus === 'no-deadline').length
  },
  upcomingDeadlines: upcomingCount + urgentDeadlineCount + todayCount,
  overdueItems: overdueCount
};

console.log('\n=== COMPLETE ===');
console.log('Meeting:', meetingData.meetingTitle);
console.log('Summary:', meetingData.summary.length, 'chars');
console.log('Action Items:', meetingData.actionItems.length);

return [{ json: meetingData }];

