const parseData = $('Parse and Enrich Data').first().json;
const actionItems = parseData.actionItems || [];
const pdfUrl = $json.pdfUrl || $json.webViewLink || '';

if (actionItems.length === 0) {
  return [];
}

// Group tasks by assignee
const grouped = {};
actionItems.forEach(item => {
  if (!grouped[item.assignee]) {
    grouped[item.assignee] = [];
  }
  grouped[item.assignee].push(item);
});

// Return one item per assignee
return Object.keys(grouped).map(assignee => ({
  json: {
    assignee: assignee,
    tasks: grouped[assignee],
    taskCount: grouped[assignee].length,
    meetingTitle: parseData.meetingTitle,
    meetingDate: parseData.meetingDate,
    pdfUrl: pdfUrl
  }
}));

const actionItems = $json.actionItems || [];
const meetingTitle = $json.meetingTitle;
const meetingDate = $json.meetingDate;
const pdfUrl = $json.pdfUrl || '';

if (actionItems.length === 0) {
  return [];
}

// Return one item per action item for task creation
return actionItems.map(item => ({
  json: {
    task: item.task,
    assignee: item.assignee,
    deadline: item.deadline,
    priority: item.priority,
    context: item.context,
    meetingTitle: meetingTitle,
    meetingDate: meetingDate,
    status: item.status,
    daysUntilDeadline: item.daysUntilDeadline
  }
}));
