parameters.jsCode:
// Parse AI-generated content
const aiResponse = JSON.parse($input.first().json.text);
const blogData = $('Clean & Extract Content4').first().json;

return {
  json: {
    // Original content
    original_title: blogData.title,
    original_url: blogData.url,
    published_date: blogData.published_date,

    // Repurposed content
    linkedin: aiResponse.linkedin_post,
    twitter: aiResponse.twitter_thread,
    instagram: aiResponse.instagram_caption,
    email: aiResponse.email_snippet,
    video: aiResponse.video_script,

    // Metadata
    repurposed_date: new Date().toISOString(),
    status: 'ready_for_review'
  }
};

// Add image suggestions to content
const content = $('Structure Output4').first().json;
const images = $input.first().json.results || [];

return {
  json: {
    ...content,
    suggested_images: images.slice(0, 3).map(img => ({
      url: img.urls.regular,
      thumb: img.urls.thumb,
      alt: img.alt_description,
      photographer: img.user.name,
      download_url: img.links.download
    }))
  }
};
