// Track which channels were used
const items = $input.all();

// Check if SMS was sent (will have twilio response)
const smsSent = items.some(item => item.json.sid || item.json.messageSid);

// Check if Email was sent (will have email response)
const emailSent = items.some(item => item.json.accepted || item.json.messageId);

// Get customer data from Prepare Messages node
const customerData = $('Prepare Messages2').first().json;

return {
  json: {
    ...customerData,
    sms_sent: smsSent,
    email_sent: emailSent,
    messages_sent_at: new Date().toISOString()
  }
};

// Extract and analyze call data
const callData = $input.first().json;

// Twilio Call Status values:
// 'completed' = answered and ended normally
// 'no-answer' = no answer or rejected
// 'busy' = busy signal
// 'canceled' = hung up while queued/ringing
// 'failed' = invalid number or error

// Check if call was answered (CallDuration > 0 means conversation happened)
const callDuration = parseInt(callData.CallDuration) || 0;
const callStatus = callData.CallStatus || '';

// Skip answered calls (completed with duration > 0)
const wasAnswered = callStatus === 'completed' && callDuration > 0;

if (wasAnswered) {
  return []; // Don't process answered calls
}

// Extract caller information (handle both camelCase and PascalCase)
const callerNumber = callData.From || callData.from || '';
const receiverNumber = callData.To || callData.to || '';
const timestamp = callData.Timestamp || callData.timestamp || new Date().toISOString();
const callTime = new Date(timestamp);
const currentHour = callTime.getHours();

// Determine business context
const isBusinessHours = currentHour >= 9 && currentHour < 17;
const dayOfWeek = callTime.getDay();
const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;

return {
  json: {
    caller_number: callerNumber,
    receiver_number: receiverNumber,
    call_time: callTime.toISOString(),
    call_time_local: callTime.toLocaleString(),
    call_status: callStatus,
    call_duration: callDuration,
    is_business_hours: isBusinessHours,
    is_weekend: isWeekend,
    call_sid: callData.CallSid || callData.callSid || '',
    urgency_score: !isBusinessHours ? 8 : 5, // Higher urgency for after-hours calls
    context: {
      time_of_day: currentHour < 12 ? 'morning' : currentHour < 17 ? 'afternoon' : 'evening',
      day_type: isWeekend ? 'weekend' : 'weekday'
    }
  }
};

// Merge call data with CRM history
const callData = $('Analyze Call Context2').first().json;
const crmData = $input.first().json;

const hasHistory = crmData && crmData.contact;
const contact = crmData?.contact || {};

return {
  json: {
    ...callData,

    // CRM Information
    is_existing_customer: hasHistory,
    customer_name: contact.name || 'Unknown',
    customer_email: contact.email || null,
    last_contact_date: contact.last_contact || null,
    customer_status: contact.status || 'new',
    previous_purchases: contact.purchase_count || 0,
    lifetime_value: contact.lifetime_value || 0,
    notes: contact.notes || '',

    // Prioritization
    priority_level: hasHistory && contact.lifetime_value > 1000 ? 'high' : hasHistory ? 'medium' : 'new',
    follow_up_urgency: hasHistory ? 'immediate' : 'standard'
  }
};

