// Parse AI response and prepare messages
const aiResponse = $input.first().json;
const customerData = $('Merge Customer Data2').first().json;

// Generate booking link (Cal.com or Calendly)
const bookingUrl = 'https://cal.com/your-business'; // Replace with your booking link
const bookingLink = `${bookingUrl}?name=${encodeURIComponent(customerData.customer_name)}&phone=${encodeURIComponent(customerData.caller_number)}`;

// Add booking link to messages
const smsWithLink = `${aiResponse.sms}\n\nBook instantly: ${bookingLink}`;
const emailWithLink = aiResponse.email_body.replace('[BOOKING_LINK]', bookingLink);

return {
  json: {
    ...customerData,

    // AI-generated messages
    sms_message: smsWithLink,
    email_subject: aiResponse.email_subject,
    email_body: emailWithLink,
    message_tone: aiResponse.tone,
    recommended_channel: aiResponse.recommended_channel,

    // Booking information
    booking_link: bookingLink,

    // Metadata
    response_generated_at: new Date().toISOString()
  }
};

