const availableArticles = $input.first().json.availableArticles
const processedArticles = $input.last().json.processedArticles

const processedURLs = processedArticles.map(i => i.url)

const remainingArticles = availableArticles.filter(a => !processedURLs.includes(a.url));

if (remainingArticles.length === 0) {
  return []; // nothing left to process
}

const randomIndex = Math.floor(Math.random() * remainingArticles.length);
return [{ json: remainingArticles[randomIndex] }];

