parameters.jsCode:
const issue = $input.item.json.issue;
const repo = $input.item.json.repository;

const fileRegex = /`([^`]*\.(js|py|ts|jsx|tsx|java|go|rb|php|cpp|c|css|html))`/g;
const mentionedFiles = [];
let match;

if (issue.body) {
  while ((match = fileRegex.exec(issue.body)) !== null) {
    mentionedFiles.push(match[1]);
  }
}

return {
  issueNumber: issue.number,
  title: issue.title,
  description: issue.body || "No description provided",
  reporter: issue.user.login,
  reporterAvatar: issue.user.avatar_url,
  labels: issue.labels.map(l => l.name).join(", ") || "none",
  url: issue.html_url,
  createdAt: issue.created_at,
  mentionedFiles: mentionedFiles.join(", ") || "No files mentioned",
  repositoryName: repo.full_name,
  repositoryOwner: repo.owner.login,
  repositoryRepo: repo.name
};

