parameters.jsCode:
const issueData = $('Extract Issue Context').item.json;
const gptResponse = $input.item.json.text;

let analysis;
try {
  analysis = JSON.parse(gptResponse);
} catch (error) {
  analysis = {
    bugSeverity: "Medium",
    category: "General",
    reproductionSteps: "See original issue",
    potentialRootCause: "Requires investigation",
    suggestedPriority: "P2",
    estimatedComplexity: "Medium",
    recommendedDeveloper: "fullstack-dev",
    estimatedHours: 8
  };
}

const developerMapping = {
  "backend-dev": "backend.dev@company.com",
  "frontend-dev": "frontend.dev@company.com",
  "fullstack-dev": "fullstack.dev@company.com",
  "devops": "devops@company.com"
};

const priorityMapping = {
  "P0": "Highest",
  "P1": "High",
  "P2": "Medium",
  "P3": "Low"
};

return {
  ...issueData,
  bugSeverity: analysis.bugSeverity,
  category: analysis.category,
  reproductionSteps: analysis.reproductionSteps,
  potentialRootCause: analysis.potentialRootCause,
  suggestedPriority: analysis.suggestedPriority,
  estimatedComplexity: analysis.estimatedComplexity,
  estimatedHours: analysis.estimatedHours,
  recommendedDeveloper: analysis.recommendedDeveloper,
  assignedDeveloper: developerMapping[analysis.recommendedDeveloper] || "triage@company.com",
  jiraLabels: [analysis.category, analysis.bugSeverity],
  jiraPriority: priorityMapping[analysis.suggestedPriority] || "Medium"
};

