// Extract and normalize ticket data from webhook payload
const ticketData = $input.item.json.body || $input.item.json;

// Handle different payload structures (Zendesk, Freshdesk, custom)
const ticket = ticketData.ticket || ticketData;

// Extract ticket ID (handle various formats)
const extractTicketId = (ticket) => {
  return ticket.id || ticket.ticket_id || ticket.number || 'UNKNOWN';
};

// Extract agent name with fallback
const extractAgentName = (ticket) => {
  if (ticket.assignee?.name) return ticket.assignee.name;
  if (ticket.agent?.name) return ticket.agent.name;
  if (ticket.assigned_to) return ticket.assigned_to;
  return 'Support Team';
};

// Extract customer info
const extractCustomerInfo = (ticket) => {
  return {
    name: ticket.requester?.name || ticket.customer?.name || 'Customer',
    email: ticket.requester?.email || ticket.customer?.email || 'N/A'
  };
};

// Extract resolution notes
const extractResolution = (ticket) => {
  if (ticket.resolution_notes) return ticket.resolution_notes;
  if (ticket.latest_comment?.body) return ticket.latest_comment.body;
  if (ticket.internal_notes) return ticket.internal_notes;
  if (Array.isArray(ticket.comments) && ticket.comments.length > 0) {
    return ticket.comments[ticket.comments.length - 1].body;
  }
  return 'Resolution details not available';
};

const customer = extractCustomerInfo(ticket);

// Return normalized data structure
return {
  json: {
    ticketId: extractTicketId(ticket),
    subject: ticket.subject || 'No Subject Provided',
    description: ticket.description || ticket.body || '',
    resolution: extractResolution(ticket),
    agentName: extractAgentName(ticket),
    customerName: customer.name,
    customerEmail: customer.email,
    priority: (ticket.priority || 'normal').toLowerCase(),
    createdAt: ticket.created_at || new Date().toISOString(),
    resolvedAt: ticket.updated_at || ticket.resolved_at || new Date().toISOString(),
    tags: ticket.tags || [],
    category: ticket.category || ticket.type || 'General Support',
    rawTicket: ticket // Keep original for reference
  }
};

// Combine ticket data with AI summary and create professional HTML document
const ticket = $('Extract Ticket Details').item.json;
const aiResponse = $input.item.json;

// Extract AI summary (handle different response formats)
let aiSummary = '';
if (aiResponse.message?.content) {
  aiSummary = aiResponse.message.content;
} else if (aiResponse.choices?.[0]?.message?.content) {
  aiSummary = aiResponse.choices[0].message.content;
} else if (typeof aiResponse === 'string') {
  aiSummary = aiResponse;
} else {
  aiSummary = JSON.stringify(aiResponse);
}

// Format dates nicely
const formatDate = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
};

// Calculate resolution time
const calculateResolutionTime = (created, resolved) => {
  const diff = new Date(resolved) - new Date(created);
  const hours = Math.floor(diff / (1000 * 60 * 60));
  const days = Math.floor(hours / 24);

  if (days > 0) return `${days} day${days > 1 ? 's' : ''}`;
  if (hours > 0) return `${hours} hour${hours > 1 ? 's' : ''}`;
  return 'Less than 1 hour';
};

const resolutionTime = calculateResolutionTime(ticket.createdAt, ticket.resolvedAt);
const currentDate = new Date().toLocaleDateString('en-US', { 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Create comprehensive HTML document
const html = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Support Ticket ${ticket.ticketId} - Case Documentation</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            line-height: 1.6;
            color: #333;
            background-color: #f5f7fa;
            padding: 0;
        }
        .page {
            background-color: white;
            max-width: 800px;
            margin: 0 auto;
            padding: 40px;
            min-height: 100vh;
        }
        .header {
            border-bottom: 4px solid #4CAF50;
            padding-bottom: 25px;
            margin-bottom: 35px;
        }
        .logo {
            color: #4CAF50;
            font-size: 28px;
            font-weight: bold;
            margin-bottom: 5px;
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .company-tagline {
            color: #666;
            font-size: 14px;
            font-style: italic;
            margin-bottom: 20px;
        }
        h1 {
            color: #2c3e50;
            margin: 10px 0;
            font-size: 32px;
            font-weight: 700;
        }
        .badges {
            margin-top: 15px;
        }
        .badge {
            display: inline-block;
            padding: 6px 14px;
            border-radius: 20px;
            font-size: 11px;
            font-weight: bold;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            margin-right: 8px;
        }
        .badge-resolved {
            background-color: #4CAF50;
            color: white;
        }
        .badge-priority-high {
            background-color: #f44336;
            color: white;
        }
        .badge-priority-urgent {
            background-color: #d32f2f;
            color: white;
        }
        .badge-priority-normal {
            background-color: #ff9800;
            color: white;
        }
        .badge-priority-low {
            background-color: #2196F3;
            color: white;
        }
        .meta-info {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            gap: 20px;
            background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
            padding: 25px;
            border-radius: 8px;
            margin-bottom: 35px;
            border: 1px solid #dee2e6;
        }
        .meta-item {
            font-size: 14px;
        }
        .meta-label {
            font-weight: 700;
            color: #495057;
            display: block;
            margin-bottom: 5px;
            text-transform: uppercase;
            font-size: 11px;
            letter-spacing: 0.5px;
        }
        .meta-value {
            color: #212529;
            font-size: 15px;
            font-weight: 500;
        }
        h2 {
            color: #4CAF50;
            border-left: 5px solid #4CAF50;
            padding-left: 15px;
            margin-top: 35px;
            margin-bottom: 20px;
            font-size: 22px;
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .section {
            margin-bottom: 30px;
            padding: 20px;
            background-color: #fafbfc;
            border-radius: 6px;
            border-left: 3px solid #e9ecef;
        }
        .section p {
            margin-bottom: 12px;
            line-height: 1.8;
        }
        .section ul {
            margin: 15px 0;
            padding-left: 25px;
        }
        .section li {
            margin-bottom: 10px;
            line-height: 1.8;
            padding-left: 5px;
        }
        .tags {
            margin-top: 20px;
            padding-top: 20px;
            border-top: 1px solid #e9ecef;
        }
        .tag {
            display: inline-block;
            background-color: #e9ecef;
            color: #495057;
            padding: 5px 12px;
            border-radius: 12px;
            font-size: 12px;
            margin-right: 8px;
            margin-bottom: 8px;
        }
        .footer {
            margin-top: 50px;
            padding-top: 25px;
            border-top: 2px solid #e9ecef;
            text-align: center;
            font-size: 12px;
            color: #6c757d;
        }
        .footer-row {
            margin-bottom: 8px;
        }
        .stats-box {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
            padding: 20px;
            border-radius: 8px;
            margin-bottom: 30px;
            text-align: center;
        }
        .stats-box .stat-label {
            font-size: 12px;
            text-transform: uppercase;
            letter-spacing: 1px;
            opacity: 0.9;
            margin-bottom: 5px;
        }
        .stats-box .stat-value {
            font-size: 24px;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <div class="page">
        <div class="header">
            <div class="logo">
                üéØ Your Company Support
            </div>
            <div class="company-tagline">Excellence in Customer Service</div>
            <h1>Support Case Documentation</h1>
            <div class="badges">
                <span class="badge badge-resolved">‚úì RESOLVED</span>
                <span class="badge badge-priority-${ticket.priority}">${ticket.priority.toUpperCase()} PRIORITY</span>
            </div>
        </div>

        <div class="stats-box">
            <div class="stat-label">Resolution Time</div>
            <div class="stat-value">‚ö° ${resolutionTime}</div>
        </div>

        <div class="meta-info">
            <div class="meta-item">
                <span class="meta-label">Ticket ID</span>
                <div class="meta-value">#${ticket.ticketId}</div>
            </div>
            <div class="meta-item">
                <span class="meta-label">Category</span>
                <div class="meta-value">${ticket.category}</div>
            </div>
            <div class="meta-item">
                <span class="meta-label">Customer</span>
                <div class="meta-value">${ticket.customerName}</div>
            </div>
            <div class="meta-item">
                <span class="meta-label">Customer Email</span>
                <div class="meta-value">${ticket.customerEmail}</div>
            </div>
            <div class="meta-item">
                <span class="meta-label">Resolved By</span>
                <div class="meta-value">${ticket.agentName}</div>
            </div>
            <div class="meta-item">
                <span class="meta-label">Resolution Date</span>
                <div class="meta-value">${formatDate(ticket.resolvedAt)}</div>
            </div>
        </div>

        <h2>üìã Subject</h2>
        <div class="section">
            <p><strong>${ticket.subject}</strong></p>
        </div>

        ${aiSummary}

        ${ticket.tags && ticket.tags.length > 0 ? `
        <div class="tags">
            <strong>Tags:</strong><br>
            ${ticket.tags.map(tag => `<span class="tag">#${tag}</span>`).join('')}
        </div>
        ` : ''}

        <div class="footer">
            <div class="footer-row">
                <strong>Document Generated:</strong> ${currentDate}
            </div>
            <div class="footer-row">
                Case #${ticket.ticketId} | Ticket opened: ${formatDate(ticket.createdAt)}
            </div>
            <div class="footer-row" style="margin-top: 15px;">
                ¬© ${new Date().getFullYear()} Your Company - Support Documentation System
            </div>
            <div class="footer-row" style="margin-top: 10px; font-style: italic;">
                This document was automatically generated by our AI-powered support documentation workflow
            </div>
        </div>
    </div>
</body>
</html>
`;

// Generate filename with timestamp
const timestamp = new Date().toISOString().split('T')[0];
const sanitizedSubject = ticket.subject
  .replace(/[^a-z0-9]/gi, '_')
  .substring(0, 50);
const fileName = `Ticket_${ticket.ticketId}_${sanitizedSubject}_${timestamp}.pdf`;

return {
  json: {
    ...ticket,
    aiSummary: aiSummary,
    htmlContent: html,
    fileName: fileName,
    resolutionTime: resolutionTime,
    documentGeneratedAt: new Date().toISOString()
  }
};

