const data = $input.all()[0].json.body;

// Validation
const errors = [];

if (!data.nama || data.nama.trim() === '') {
  errors.push('Nama wajib diisi');
}

if (!data.email || !data.email.includes('@')) {
  errors.push('Email tidak valid');
}

if (!data.no_hp || data.no_hp.length < 10) {
  errors.push('Nomor HP tidak valid');
}

if (!data.jumlah_tiket || data.jumlah_tiket < 1) {
  errors.push('Jumlah tiket minimal 1');
}

if (!data.payment_method) {
  errors.push('Payment method wajib dipilih');
}

// Return error if validation fails
if (errors.length > 0) {
  return [{
    json: {
      valid: false,
      validation_error: true,
      errors: errors
    }
  }];
}

// Transform data for sheet
return [{
  json: {
    valid: true,
    validation_error: false,
    Nama: data.nama,
    "No Hp": data.no_hp,
    Email: data.email,
    "Jumlah TIket": data.jumlah_tiket,
    "Harga Tiket": data.total_price,
    "Payment Method ": data.payment_method,
    "Payment Status": "PENDING",
    "Email Sent": "NO",
    "Ticked ID": ""
  }
}];

