const items = $input.all();
const output = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i].json;

  // Parse jumlah tiket
  const jumlahTiket = parseInt(item["Jumlah TIket"]) || 1;

  // Generate ALL ticket IDs first for this person
  const allTicketsForThisPerson = [];

  for (let ticketNum = 1; ticketNum <= jumlahTiket; ticketNum++) {
    const now = new Date();
    const timestamp = now.toISOString().slice(0,10).replace(/-/g, '');
    const rowNum = item.row_number || (i + 2);
    const hash = Math.random().toString(36).substring(2, 10).toUpperCase();

    const ticketId = `TL-${timestamp}-${String(rowNum).padStart(4, '0')}-${ticketNum}-${hash}`;
    allTicketsForThisPerson.push(ticketId);

    const qrData = JSON.stringify({
      event: "TABRAK_LARI",
      ticket_id: ticketId,
      name: item.Nama,
      email: item.Email,
      ticket_number: ticketNum,
      total_tickets: jumlahTiket,
      timestamp: now.toISOString(),
      hash: hash
    });

    output.push({
      json: {
        ...item,
        ticket_id: ticketId,
        ticket_number: ticketNum,
        total_tickets: jumlahTiket,
        qr_data: qrData,
        qr_data_url_encoded: encodeURIComponent(qrData),
        event_name: "TABRAK LARI",
        event_date: "15 November 2025",
        event_location: "GOR Pontianak"
      }
    });
  }
}

return output;

