const items = $input.all();
const grouped = {};

for (let item of items) {
  const email = item.json.Email;

  if (!grouped[email]) {
    grouped[email] = {
      Email: email,
      ticket_ids: []
    };
  }

  grouped[email].ticket_ids.push(item.json.ticket_id);
}

const output = Object.values(grouped).map(item => ({
  json: {
    Email: item.Email,
    ticket_ids_combined: item.ticket_ids.join(", ")
  }
}));

return output;
