/**
 * n8n Code node (JavaScript)
 * 入力:  $input.first().json.output に Markdown風の長文（実改行 or リテラル "\n"）
 * 出力:  最初のアイテムに以下を格納
 *   - json.blocks: Array<Block>（Block Kit の配列）
 *   - json.slackPayload: { blocks: Array<Block> }  // ← UI Builder と同じトップレベル
 *   - json.text_fallback: Slack text 用フォールバック
 *
 * 次ノード（Slack 送信例）:
 *   - Body JSON に {{$json.slackPayload}} をそのまま使う
 *   - もしくは blocks: {{$json.blocks}}, text: {{$json.text_fallback}}
 */

// ===== Utils =====
function toActualNewlines(s) {
  if (typeof s !== 'string') return '';
  return s.replace(/\r\n?/g, '\n').replace(/\\n/g, '\n');
}

// インライン: *bold* / **bold**, _italic_, `code`, [text](url)
function tokenizeInline(text) {
  const tokens = [];
  let i = 0;

  const pushPlain = (t) => { if (t) tokens.push({ type: 'text', text: t }); };

  const patterns = [
    { // code
      re: /`([^`]+)`/g,
      build: (m) => ({ type: 'text', text: m[1], style: { code: true } }),
    },
    { // bold **...**
      re: /\*\*([^*]+)\*\*/g,
      build: (m) => ({ type: 'text', text: m[1], style: { bold: true } }),
    },
    { // bold *...* （先頭の箇条書き "* " と衝突しないよう調整）
      re: /(^|[^*])\*([^*\n]+)\*(?!\*)/g,
      build: (m) => [{ type: 'text', text: m[1] || '' }, { type: 'text', text: m[2], style: { bold: true } }],
      isComposite: true,
    },
    { // italic _..._
      re: /_([^_]+)_/g,
      build: (m) => ({ type: 'text', text: m[1], style: { italic: true } }),
    },
    { // link [text](url)
      re: /\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g,
      build: (m) => ({ type: 'link', url: m[2], text: m[1] }),
    },
  ];

  while (i < text.length) {
    let earliest = null, pat = null;
    for (const p of patterns) {
      p.re.lastIndex = i;
      const m = p.re.exec(text);
      if (m && (!earliest || m.index < earliest.index)) {
        earliest = m; pat = p;
      }
    }
    if (!earliest) { pushPlain(text.slice(i)); break; }

    if (earliest.index > i) pushPlain(text.slice(i, earliest.index));

    if (pat.isComposite) {
      const built = pat.build(earliest);
      for (const b of built) if (b.text !== '') tokens.push(b);
    } else {
      tokens.push(pat.build(earliest));
    }

    i = earliest.index + earliest[0].length;
  }

  return tokens.length ? tokens : [{ type: 'text', text }];
}

function makeSectionElementsFromText(t) {
  return tokenizeInline(t);
}

// ===== Block builders =====
function headerBlock(text) {
  return { type: 'header', text: { type: 'plain_text', text: text.slice(0, 150) } };
}
function paragraphBlock(text) {
  return { type: 'rich_text', elements: [{ type: 'rich_text_section', elements: makeSectionElementsFromText(text) }] };
}
function quoteBlock(lines) {
  const elements = lines.map(l => ({ type: 'rich_text_section', elements: makeSectionElementsFromText(l) }));
  return { type: 'rich_text', elements: [{ type: 'rich_text_quote', elements }] };
}
function preBlock(lines) {
  return { type: 'rich_text', elements: [{ type: 'rich_text_preformatted', elements: [{ type: 'text', text: lines.join('\n') }] }] };
}
function listBlock(style, indent, sections) {
  return { type: 'rich_text', elements: [{ type: 'rich_text_list', style, indent, elements: sections }] };
}

// ===== Parser =====
function convertMarkdownToBlocks(mdRaw) {
  const md = toActualNewlines(mdRaw);
  const lines = md.split('\n');
  const blocks = [];

  let inCode = false, codeBuf = [];
  let inQuote = false, quoteBuf = [];

  // リスト状態（style/indent で束ねる）
  let listActive = false, listStyle = null, listIndent = 0, listItems = [];

  const flushQuote = () => {
    if (inQuote && quoteBuf.length) blocks.push(quoteBlock(quoteBuf));
    inQuote = false; quoteBuf = [];
  };
  const flushList = () => {
    if (listActive && listItems.length) blocks.push(listBlock(listStyle, listIndent, listItems));
    listActive = false; listStyle = null; listIndent = 0; listItems = [];
  };
  const startList = (style, indent) => {
    if (!listActive || listStyle !== style || listIndent !== indent) {
      flushList();
      listActive = true; listStyle = style; listIndent = indent; listItems = [];
    }
  };

  const parseListLine = (line) => {
    const leading = (line.match(/^\s*/)[0] || '').length;
    const trimmed = line.trim();
    if (/^[-*•]\s+/.test(trimmed)) {
      return { style: 'bullet', indent: Math.floor(leading / 2), text: trimmed.replace(/^[-*•]\s+/, '') };
    }
    const m = trimmed.match(/^(\d+)\.\s+(.+)$/);
    if (m) {
      return { style: 'ordered', indent: Math.floor(leading / 2), text: m[2] };
    }
    return null;
  };

  for (let i = 0; i < lines.length; i++) {
    const raw = lines[i];

    // ``` code fence
    if (/^\s*```/.test(raw)) {
      flushQuote(); flushList();
      if (!inCode) { inCode = true; codeBuf = []; }
      else { blocks.push(preBlock(codeBuf)); inCode = false; codeBuf = []; }
      continue;
    }
    if (inCode) { codeBuf.push(raw); continue; }

    // 空行は区切り
    if (/^\s*$/.test(raw)) {
      flushQuote(); flushList();
      continue;
    }

    // 引用
    if (/^\s*>+\s?/.test(raw)) {
      flushList();
      const q = raw.replace(/^\s*>+\s?/, '');
      inQuote = true; quoteBuf.push(q);
      continue;
    } else if (inQuote) {
      flushQuote();
    }

    // 見出し（#〜### or **見出し** 単独行）
    if (/^\s*#{1,3}\s+/.test(raw) || /^\s*\*\*(.+?)\*\*\s*$/.test(raw)) {
      flushQuote(); flushList();
      const text = /^\s*#{1,3}\s+/.test(raw)
        ? raw.replace(/^\s*#{1,3}\s+/, '').trim()
        : (raw.match(/^\s*\*\*(.+?)\*\*\s*$/)[1] || '').trim();
      blocks.push(headerBlock(text));
      continue;
    }

    // リスト
    const li = parseListLine(raw);
    if (li) {
      flushQuote();
      startList(li.style, li.indent);
      listItems.push({ type: 'rich_text_section', elements: makeSectionElementsFromText(li.text) });
      continue;
    } else {
      flushList();
    }

    // 通常段落
    blocks.push(paragraphBlock(raw));
  }

  // 残り
  if (inCode && codeBuf.length) blocks.push(preBlock(codeBuf));
  flushQuote(); flushList();

  return blocks;
}

// ===== Main =====
const items = $input.all();
const first = $input.first();
const source = first?.json?.output ?? '';

const blocks = convertMarkdownToBlocks(source);

// text_fallback: 最初の header / paragraph を抽出
let textFallback = 'Message';
for (const b of blocks) {
  if (b.type === 'header') { textFallback = b.text?.text?.slice(0, 300) || textFallback; break; }
  if (b.type === 'rich_text') {
    const el = b.elements?.[0];
    if (el?.type === 'rich_text_section') {
      const s = (el.elements || [])
        .map(e => e.type === 'text' ? e.text : (e.type === 'link' ? (e.text || e.url) : ''))
        .join('');
      if (s) { textFallback = s.slice(0, 300); break; }
    }
  }
}

// 返却フォーマット（UI Builder と同じトップに blocks）
first.json.blocks = blocks;                 // 配列（必要ならこちらを直接使う）
first.json.slackPayload = { blocks };       // ← 期待どおりの { "blocks": [...] }
first.json.text_fallback = textFallback;

return items;
