parameters.jsCode:
// Expect shape: { choices: [ { message: { content: "<json string>" } } ] }
let content =
  $json.choices?.[0]?.message?.content ??
  $json.choices?.[0]?.delta?.content ??
  null;

if (typeof content !== 'string') {
  throw new Error('Perplexity: missing choices[0].message.content (string).');
}

// ---------- Robust Parser with Repair ----------
function parsePerplexityJSON(s) {
  let t = String(s).trim();

  // 1. Remove code fences like ```json ... ```
  t = t.replace(/```[a-z]*\n?/gi, '').replace(/```/g, '').trim();

  // 2. Remove leading "+<number>," if present
  t = t.replace(/^\+\d+,\s*/, '');

  // 3. Strip any "Preview:" debug sections
  t = t.replace(/Preview[^{}"]*({.*)$/s, '$1');

  // 4. Trim to first { ... last }
  const firstBrace = t.indexOf('{');
  const lastBrace = t.lastIndexOf('}');
  if (firstBrace >= 0 && lastBrace > firstBrace) {
    t = t.slice(firstBrace, lastBrace + 1);
  }

  // 5. Remove dangling commas
  t = t.replace(/,\s*([}\]])/g, '$1');

  // Try parsing
  try {
    return JSON.parse(t);
  } catch (e) {
    // Attempt repair: close any unterminated string
    let repaired = t.replace(/"[^"]*$/s, '""}');
    try {
      return JSON.parse(repaired);
    } catch {
      throw new Error("Unable to parse cleaned JSON. First 300 chars:\n" + t.slice(0, 300));
    }
  }
}

// ---------- Main ----------
let research;
try {
  research = parsePerplexityJSON(content);
} catch (err) {
  return [{
    json: {
      error: "Perplexity content is not valid JSON",
      details: err.message,
      raw_preview: content.slice(0, 600)
    }
  }];
}

// -------- Normalization / Back-compat --------
const ensureArray = (v) => (Array.isArray(v) ? v : []);
const ensureObj   = (v) => (v && typeof v === 'object' ? v : {});

research.indices      = ensureArray(research.indices);
research.rates        = ensureArray(research.rates);
research.fx           = ensureArray(research.fx);
research.commodities  = ensureArray(research.commodities);
research.crypto       = ensureArray(research.crypto);
research.earnings     = ensureArray(research.earnings);
research.geopolitics  = ensureArray(research.geopolitics);
research.week_ahead   = ensureArray(research.week_ahead);

if (!research.sectors) research.sectors = { top3: [], bottom3: [] };
research.sectors.top3    = ensureArray(research.sectors.top3);
research.sectors.bottom3 = ensureArray(research.sectors.bottom3);

if (!research.macro_policy) {
  research.macro_policy = { us: [], eu: [], uk: [], jp: [], cn: [] };
} else {
  research.macro_policy.us = ensureArray(research.macro_policy.us);
  research.macro_policy.eu = ensureArray(research.macro_policy.eu);
  research.macro_policy.uk = ensureArray(research.macro_policy.uk);
  research.macro_policy.jp = ensureArray(research.macro_policy.jp);
  research.macro_policy.cn = ensureArray(research.macro_policy.cn);
}

const dba = ensureObj(research.drivers_by_asset);
if (!research.asset_highlights) {
  research.asset_highlights = {
    equities:     ensureArray(dba.equities),
    fixed_income: ensureArray(dba.fixed_income),
    fx:           ensureArray(dba.fx),
    commodities:  ensureArray(dba.commodities),
    crypto:       ensureArray(dba.crypto),
  };
} else {
  research.asset_highlights.equities     = ensureArray(research.asset_highlights.equities);
  research.asset_highlights.fixed_income = ensureArray(research.asset_highlights.fixed_income);
  research.asset_highlights.fx           = ensureArray(research.asset_highlights.fx);
  research.asset_highlights.commodities  = ensureArray(research.asset_highlights.commodities);
  research.asset_highlights.crypto       = ensureArray(research.asset_highlights.crypto);
}

if (research.note != null && typeof research.note !== 'string') {
  research.note = String(research.note);
}

// Done
return [{ json: research }];

const now = new Date();               // 08:00 Friday
const day = now.getDay();             // 5 on Fri
const lastSun = new Date(now); lastSun.setDate(now.getDate() - day); lastSun.setHours(23,59,59,999);
const lastMon = new Date(lastSun); lastMon.setDate(lastSun.getDate() - 6); lastMon.setHours(0,0,0,0);
const nextMon = new Date(lastSun); nextMon.setDate(lastSun.getDate()+1); nextMon.setHours(0,0,0,0);
const nextSun = new Date(nextMon); nextSun.setDate(nextMon.getDate()+6); nextSun.setHours(23,59,59,999);

// simple idempotency key
const h = require('crypto').createHash('sha256');
h.update(`${lastMon.toISOString()}_${lastSun.toISOString()}`);
const run_key = h.digest('hex').slice(0,12);

return [{ json: {
  week_label: `Week of ${lastMon.toISOString().slice(0,10)}`,
  start_iso: lastMon.toISOString(),
  end_iso: lastSun.toISOString(),
  next_start_iso: nextMon.toISOString(),
  next_end_iso: nextSun.toISOString(),
  run_key
}}];

// ---------- tiny helpers ----------
function get(node, path) {
  try {
    const o = $node[node].json;
    return path ? path.split('.').reduce((a, k) => a?.[k], o) : o;
  } catch { return undefined; }
}

function readFromHB(hb, key) {
  if (!hb) return undefined;
  if (typeof hb === 'string') {
    try { const p = JSON.parse(hb); return p?.[key]; } catch { return undefined; }
  }
  if (hb[key]) return hb[key];
  const m = hb.message;
  if (m && typeof m === 'object') {
    if (m[key]) return m[key];
    const c = m.content ?? m.text ?? '';
    if (typeof c === 'string') { try { const p = JSON.parse(c); return p?.[key]; } catch {} }
    else if (c && typeof c === 'object') { return c[key]; }
  }
  return undefined;
}

function parseLLMJson(nodeName) {
  const n = get(nodeName);
  if (!n) return;
  if (typeof n === 'string') { try { return JSON.parse(n); } catch {} }
  if (n.message) {
    const c = n.message.content ?? n.message.text ?? n.message;
    if (typeof c === 'string') { try { return JSON.parse(c); } catch {} }
    if (typeof c === 'object') return c;
  }
  if (n.json && typeof n.json === 'object') return n.json;
  if (typeof n === 'object') return n;
}

// ---------- prefer new builder → fallbacks ----------
const hb = get("HTML Builder LLM") || get("HTML Builder") || {};
const html =
  readFromHB(hb, "html") ??
  get("Build Draft HTML", "html") ??
  $json.html;

const weekLabel =
  get("Week ranges + run_key", "week_label") ||
  $json.week_label || "";

const subject =
  (readFromHB(hb, "subject") ??
   get("Build Draft HTML", "subject") ??
   (weekLabel ? `Weekly Markets — ${weekLabel}` : "Weekly Markets"))
  .toString()
  .trim();

if (!html) throw new Error("No HTML from builders or payload. Run prior nodes.");

// ---------- QC → lightweight warnings ----------
const qc = get("QC LLM") || {};
const warnings = [];
if (Array.isArray(qc.facts) && qc.facts.length) warnings.push(`Fact issues: ${qc.facts.length}`);
if (Array.isArray(qc.compliance) && qc.compliance.length) warnings.push(`Compliance flags: ${qc.compliance.length}`);
if (qc.material_issue) warnings.push("Material issues present");

// ---------- enrich for stateless approve/revise ----------
const editor_json =
  $json.editor_json ?? parseLLMJson("Editor LLM");

const research =
  $json.research ??
  get("Parse Perplexity JSON") ??
  get("Research JSON");

const wk = get("Week ranges + run_key") || {};
const week_meta = {
  week_label: wk.week_label ?? $json.week_label,
  start_iso:  wk.start_iso  ?? $json.start_iso,
  end_iso:    wk.end_iso    ?? $json.end_iso,
  run_key:    wk.run_key    ?? $json.run_key,
};

// Everything the webhook needs in one canon payload
const fullContext = {
  ...$json,            // keep upstream vars (safe – last-write wins downstream)
  subject,
  html,
  warnings,
  editor_json,
  research,
  week_meta,
};

// Single, URL-safe encoding for emails/links
const encoded_context = Buffer.from(JSON.stringify(fullContext)).toString("base64url");

// Output for downstream (email + storage)
return [{
  json: { subject, html, warnings, encoded_context }
}];

