(() => {
  const qp = new URLSearchParams($json.query?.__rawQuery || $json.query || '');
  const run    = qp.get('run')    || '';
  const action = qp.get('action') || 'revise';
  const postUrl = 'https://[YOURDOMAIN].app.n8n.cloud/webhook/newsletter-approval-submit';

  return `<!doctype html>
<html>
<head>
  <meta charset="utf-8"/>
  <title>Newsletter Approval</title>
  <meta name="viewport" content="width=device-width,initial-scale=1"/>
  <style>
    body{font-family:-apple-system,Segoe UI,Roboto,Arial,sans-serif;margin:0;padding:24px;background:#f6f7f9}
    .card{max-width:720px;margin:0 auto;border:1px solid #e5e5e5;border-radius:8px;padding:20px;background:#fff}
    .muted{color:#6b7280}
    .btn{display:inline-block;padding:10px 16px;border:0;border-radius:6px;color:#fff;font-weight:600;text-decoration:none;cursor:pointer}
    .btn-approve{background:#16a34a}
    .btn-revise{background:#111}
    textarea{width:100%;box-sizing:border-box}
  </style>
</head>
<body>
  <div class="card">
    <h2 style="margin:0 0 8px">Weekly Newsletter — Approval</h2>
    <p class="muted" style="margin:0 0 16px">Run: ${run || 'n/a'}</p>

    <form id="f" method="POST" action="${postUrl}">
      <input type="hidden" name="run" value="${run}"/>
      <input type="hidden" id="ctx" name="context" value=""/>
      <input type="hidden" id="act" name="action" value="${action}"/>

      <div id="reviseBox" style="margin:16px 0;display:${action==='revise'?'block':'none'}">
        <label for="notes"><strong>Revision notes</strong></label>
        <textarea id="notes" name="notes" rows="7" placeholder="Be specific about what to change..."></textarea>
        <div style="margin-top:12px">
          <button class="btn btn-revise" type="submit">Send revision</button>
        </div>
      </div>

      <div id="approveBox" style="display:${action==='approve'?'block':'none'}">
        <p>Approving will create the final Gmail draft.</p>
        <button class="btn btn-approve" type="submit">Approve</button>
      </div>
    </form>

    <p class="muted" style="margin-top:16px">Tip: the large context is carried in the URL hash, not in the query.</p>
  </div>

  <script>
    (function(){
      // pull context from #ctx=... (preferred), fall back to ?context=
      var h = location.hash || "";
      var ctx = "";
      if (h.indexOf("#ctx=") === 0) {
        try { ctx = decodeURIComponent(h.slice(5)); } catch(e) { ctx = h.slice(5); }
      } else {
        var q = new URLSearchParams(location.search);
        ctx = q.get("context") || "";
      }
      var el = document.getElementById("ctx"); if (el) el.value = ctx;

      // auto-submit if action=approve
      if ("${action}" === "approve") {
        try { document.getElementById("f").submit(); } catch(e) {}
      }
    })();
  </script>
</body>
</html>`;
})()
}}

