// --- helpers ---
function b64urlToUtf8(s) {
  let b = String(s || '').replace(/-/g, '+').replace(/_/g, '/');
  const pad = b.length % 4 ? 4 - (b.length % 4) : 0;
  if (pad) b += '='.repeat(pad);
  return b ? Buffer.from(b, 'base64').toString('utf8') : '';
}
function safeJsonParse(txt) {
  try { return JSON.parse(txt); } catch { return {}; }
}

// --- inputs from GET/POST ---
const actionRaw = $json.body?.action ?? $json.action ?? $json.query?.action ?? '';
const action = String(actionRaw || '').toLowerCase();

// context may be in POST body, query, or current json
const rawCtx = $json.body?.context ?? $json.context ?? $json.query?.context ?? '';
const ctx = rawCtx
  ? safeJsonParse(/^\s*\{/.test(rawCtx) ? rawCtx : b64urlToUtf8(rawCtx))
  : {};

// notes only arrive on the revision form POST
let notes = $json.body?.notes ?? $json.notes ?? ctx.notes ?? '';
if (typeof notes !== 'string') notes = JSON.stringify(notes || '');

// --- merge contract (always present for downstream LLMs) ---
const week_meta = ctx.week_meta ?? $json.week_meta ?? {
  start_iso:  $json.start_iso  ?? '',
  end_iso:    $json.end_iso    ?? '',
  week_label: $json.week_label ?? '',
  run_key:    $json.run_key    ?? ''
};

const research    = ctx.research    ?? $json.research    ?? {};
const editor_json = ctx.editor_json ?? $json.editor_json ?? null;

const subjIn = (ctx.subject ?? $json.subject ?? 'Weekly Markets').toString();
let subject = subjIn.replace(/\[(?:Preview|Test|Approve[^\]]*|approved)\]\s*/gi, '').trim();
if (action === 'approve') subject = `[approved] ${subject}`;

const html = (ctx.html ?? $json.html ?? '').toString();

// Return a clean, self-sufficient payload
return [{
  json: {
    action,
    subject,
    html,
    research,
    editor_json,
    week_meta,
    notes,
    // keep original fields too in case other nodes read them
    ...$json
  }
}];

