parameters.jsCode:
// Inputs:
// - $json contains fields from Normalize + Decode Context
//   expected: run (run_key), encoded_context (optional), any prior context (week_meta, research, subject, html, etc.)
// - Notes may arrive as: $json.notes OR $json.revision_notes

const MAX_NOTES = 2000;

const nowIso = new Date().toISOString();

// 1) read existing carried context (already decoded by "Decode Context")
const ctx = { ...$json }; // includes prior subject/html/research/week_meta if present

// 2) normalize notes
let notes = (ctx.notes ?? ctx.revision_notes ?? "").toString().trim();

// compact whitespace + hard cap
notes = notes.replace(/\s+/g, " ").slice(0, MAX_NOTES);

// 3) revision bookkeeping
const prevRev = Number(ctx.rev_no || 0);
const rev_no = Number.isFinite(prevRev) ? prevRev + 1 : 1;

// 4) build a minimal, predictable merge
// keep run_key stable; prefer explicit ctx.run_key, else ctx.run
const run_key = ctx.run_key || ctx.run || "";

// 5) append notes history (optional but useful)
const revision_history = Array.isArray(ctx.revision_history) ? ctx.revision_history.slice(0, 20) : [];
if (notes) {
  revision_history.unshift({
    rev_no,
    at: nowIso,
    notes
  });
}

// 6) final merged context exposed to downstream nodes
const merged = {
  ...ctx,

  // canonical IDs / meta
  run_key,
  rev_no,
  last_updated_at: nowIso,

  // carry notes in consistent fields
  revision_notes: notes,
  notes, // keep duplicate key for templates

  // keep a bounded history for audit/debug
  revision_history,
};

// OPTIONAL: nuke large blobs you don't want to carry across runs
// e.g., if your upstream adds big raw JSONs, strip them here to keep URLs small.
// delete merged.raw_perplexity_dump;

return [{ json: merged }];

function b64urlToUtf8(s) {
  let b = s.replace(/-/g, '+').replace(/_/g, '/');
  const pad = b.length % 4 ? 4 - (b.length % 4) : 0;
  if (pad) b += '='.repeat(pad);
  return Buffer.from(b, 'base64').toString('utf8');
}

const raw = String($json.body?.context || $json.query?.context || '').trim();
let decoded = {};
try {
  const txt = raw.startsWith('{') ? raw : b64urlToUtf8(raw);
  decoded = JSON.parse(txt);
} catch (e) {
  decoded = {};
}

return [{
  json: {
    ...$json,
    ...decoded,                 // should include subject, html, editor_json, research, week_meta
    subject: decoded.subject || $json.subject || '',
    html: decoded.html || $json.html || ''
  }
}];

