=<!doctype html><meta charset="utf-8">
<title>Newsletter Approval</title>
<body style="font-family: system-ui, -apple-system, Segoe UI, Arial, sans-serif; margin:24px;">
<script>
(function () {
  const q = new URLSearchParams(location.search);
  const action = (q.get('action') || 'approve').toLowerCase();
  let ctx = "";
  if (location.hash.startsWith('#ctx=')) {
    try { ctx = decodeURIComponent(location.hash.slice(5)); } catch (e) { ctx = location.hash.slice(5); }
  }
  if (!ctx) ctx = q.get('context') || '';

  if (action === 'approve') {
    // Auto-submit straight to POST for approve
    const f = document.createElement('form');
    f.method = 'post';
    f.action = '/webhook/newsletter-approval-submit';
    f.innerHTML = `
      <input type="hidden" name="run" value="${q.get('run')||''}">
      <input type="hidden" name="action" value="approve">
      <input type="hidden" name="context" value="${ctx}">
    `;
    document.body.append('Finalizing approval…'); 
    document.body.appendChild(f); f.submit();
    return;
  }

  // action = revise → show the notes form (no auto-submit)
