// Get input data
const inputData = $input.all();

// Function to convert array to numbered list with emojis
function formatList(items, emoji = '') {
  return items.map((item, index) => `${index + 1}. ${item} ${emoji}`).join('\n');
}

// Function to convert array to bullet list with checkmarks
function formatBenefits(items) {
  return items.map(item => `‚úÖ ${item}`).join('\n');
}

// Function to convert array to bullet list with asterisks
function formatFeatures(items) {
  return items.map(item => `* ${item}`).join('\n');
}

// Function to convert array to bullet list with emojis
function formatAudience(items) {
  const emojis = ['üë©‚Äçüíº', 'üîÑ', 'üè¢', 'üåê', 'üìä'];
  return items.map((item, index) => `* ${item} ${emojis[index] || '‚ú®'}`).join('\n');
}

// Function to convert setup instructions
function formatSetup(items) {
  const emojis = ['üîë', '‚öôÔ∏è', 'üïí', 'üì¶', '‚úÖ', 'üöÄ'];
  return items.map((item, index) => `${index + 1}. ${item} ${emojis[index] || 'üìå'}`).join('\n');
}

// Process each item in the input
const results = inputData.map(item => {
  const data = item.json.output;

  // Build the formatted output
  let formattedOutput = `**Description**\n`;
  formattedOutput += `${data.overview} üíåüìäüí¨\n\n`;

  formattedOutput += `**What This Template Does**\n`;
  formattedOutput += formatList(data.what_this_template_does, '') + '\n\n';

  formattedOutput += `**Key Benefits**\n`;
  formattedOutput += formatBenefits(data.key_benefits) + '\n\n';

  formattedOutput += `**Features**\n`;
  formattedOutput += formatFeatures(data.features) + '\n\n';

  formattedOutput += `**Requirements**\n`;
  formattedOutput += data.requirements.map(item => `* ${item}`).join('\n') + '\n\n';

  formattedOutput += `**Target Audience**\n`;
  formattedOutput += formatAudience(data.target_audience) + '\n\n';

  formattedOutput += `**Step-by-Step Setup Instructions**\n`;
  formattedOutput += formatSetup(data.setup_instructions);

  return {
    json: {
      title: data.title,
      formatted_description: formattedOutput
    }
  };
});

return results;

