function organizePresentationData(input) {
  try {
    // Get the raw output from n8n input
    let rawOutput = input;

    // If the input is wrapped in ```json blocks, clean it
    if (typeof rawOutput === 'string') {
      rawOutput = rawOutput.replace(/```json\s*/, '').replace(/```\s*$/, '').trim();
    }

    // Parse the JSON if it's a string
    const parsedData = typeof rawOutput === 'string' ? JSON.parse(rawOutput) : rawOutput;

    if (!parsedData.slides || !Array.isArray(parsedData.slides)) {
      throw new Error('Invalid input: slides array not found');
    }

    // Organize the data into structured format
    const organizedData = {
      metadata: {
        totalSlides: parsedData.slides.length,
        generatedAt: new Date().toISOString(),
        presentationType: 'business_proposal'
      },
      titleSlide: null,
      contentSlides: [],
      nextStepsSlide: null
    };

    // Process each slide
    parsedData.slides.forEach(slide => {
      const slideNumber = slide.slide_number || 0;

      if (slideNumber === 1) {
        // Title slide (slide 1)
        organizedData.titleSlide = {
          slideNumber: slideNumber,
          title: slide.title || '',
          subtitle: slide.subtitle || '',
          date: slide.date || '',
          meetingDetails: slide.meetingDetails || '',
          type: 'title'
        };
      } else if (slideNumber === 9 || slide.nextSteps) {
        // Next steps slide (slide 9)
        organizedData.nextStepsSlide = {
          slideNumber: slideNumber,
          title: slide.title || '',
          nextSteps: slide.nextSteps ? slide.nextSteps.map(step => ({
            stepNumber: step.step_number || 0,
            subtitle: step.subtitle || '',
            description: step.description || ''
          })) : [],
          type: 'next_steps'
        };
      } else {
        // Content slides (slides 2-8)
        organizedData.contentSlides.push({
          slideNumber: slideNumber,
          title: slide.title || '',
          body: slide.body || [],
          bodyCount: (slide.body || []).length,
          type: slideNumber === 2 ? 'agenda' : 'content'
        });
      }
    });

    // Sort content slides by slide number
    organizedData.contentSlides.sort((a, b) => a.slideNumber - b.slideNumber);

    // Add summary statistics
    organizedData.summary = {
      hasTitleSlide: !!organizedData.titleSlide,
      contentSlidesCount: organizedData.contentSlides.length,
      hasNextStepsSlide: !!organizedData.nextStepsSlide,
      totalBulletPoints: organizedData.contentSlides.reduce((sum, slide) => sum + slide.bodyCount, 0),
      agendaItems: organizedData.contentSlides.find(slide => slide.type === 'agenda')?.bodyCount || 0
    };

    // Create flat arrays for easy n8n processing
    organizedData.allSlides = [];

    if (organizedData.titleSlide) {
      organizedData.allSlides.push(organizedData.titleSlide);
    }

    organizedData.allSlides.push(...organizedData.contentSlides);

    if (organizedData.nextStepsSlide) {
      organizedData.allSlides.push(organizedData.nextStepsSlide);
    }

    // Create bullet points array for processing
    organizedData.allBulletPoints = [];
    organizedData.contentSlides.forEach(slide => {
      slide.body.forEach((bullet, index) => {
        organizedData.allBulletPoints.push({
          slideNumber: slide.slideNumber,
          slideTitle: slide.title,
          bulletIndex: index + 1,
          bulletText: bullet
        });
      });
    });

    // Create next steps array for processing
    organizedData.allNextSteps = [];
    if (organizedData.nextStepsSlide && organizedData.nextStepsSlide.nextSteps) {
      organizedData.allNextSteps = organizedData.nextStepsSlide.nextSteps.map(step => ({
        slideNumber: organizedData.nextStepsSlide.slideNumber,
        slideTitle: organizedData.nextStepsSlide.title,
        ...step
      }));
    }

    return organizedData;

  } catch (error) {
    return {
      error: true,
      message: `Failed to organize presentation data: ${error.message}`,
      originalInput: input,
      timestamp: new Date().toISOString()
    };
  }
}

// Usage in n8n Code Node:
const organizedData = organizePresentationData($input.first().json.output);

// Return as array of objects for n8n
return [
  {
    json: organizedData
  }
];

// Export for testing
if (typeof module !== 'undefined' && module.exports) {
  module.exports = organizePresentationData;
}
