// Hilfsfunktionen
function getRawContent(item) {
  const j = item.json || {};
  return (
    j?.message?.content ??
    j?.data?.[0]?.message?.content ??
    j?.choices?.[0]?.message?.content ??
    j?.content ??
    ''
  );
}

function extractJson(raw) {
  if (typeof raw !== 'string') return null;
  // Entferne Markdown-Fences ```json ... ```
  let cleaned = raw.replace(/```json/gi, '').replace(/```/g, '').trim();
  const m = cleaned.match(/\{[\s\S]*\}/);
  return m ? m[0] : null;
}

function clamp1to5(v) {
  const n = Math.round(Number(v));
  if (Number.isNaN(n)) return 1;
  return Math.max(1, Math.min(5, n));
}

function ensureString(val) {
  if (val === undefined || val === null) return "";
  return String(val).trim();
}

// Starte mit dem ersten Item (typisch: 1 LLM-Response)
const raw = getRawContent(items[0]);
const jsonStr = extractJson(raw);
if (!jsonStr) {
  throw new Error('Kein JSON im LLM-Output gefunden.');
}

let obj;
try {
  obj = JSON.parse(jsonStr);
} catch (e) {
  throw new Error('JSON nicht parsebar: ' + e.message);
}

// Default-Werte einsetzen
obj.meeting_invitees_name = ensureString(obj.meeting_invitees_name);
obj.meeting_invitees_email = ensureString(obj.meeting_invitees_email);
obj.crm_used_past = ensureString(obj.crm_used_past);
obj.pain_points = ensureString(obj.pain_points);
obj.feature_requested = ensureString(obj.feature_requested);
obj.what_i_did_well = ensureString(obj.what_i_did_well);
obj.what_to_improve = ensureString(obj.what_to_improve);
obj.notes = ensureString(obj.notes);

// Scores validieren
const s = obj.scores || {};
s.discovery = clamp1to5(s.discovery);
s.onboarding_clarity = clamp1to5(s.onboarding_clarity);
s.objection_handling = clamp1to5(s.objection_handling);
s.engagement_rapport = clamp1to5(s.engagement_rapport);
s.close_next_steps = clamp1to5(s.close_next_steps);
obj.scores = s;

// Gesamtwerte neu berechnen
const overall = s.discovery + s.onboarding_clarity + s.objection_handling + s.engagement_rapport + s.close_next_steps;
obj.overall_score_25 = overall;
obj.overall_score_percent = Math.round((overall / 25) * 100);

// Output flatten fÃ¼r Sheets/Airtable etc.
const out = {
  meeting_invitees_name: obj.meeting_invitees_name,
  meeting_invitees_email: obj.meeting_invitees_email,
  crm_used_past: obj.crm_used_past,
  pain_points: obj.pain_points,
  feature_requested: obj.feature_requested,
  what_i_did_well: obj.what_i_did_well,
  what_to_improve: obj.what_to_improve,
  scores_discovery: s.discovery,
  scores_onboarding_clarity: s.onboarding_clarity,
  scores_objection_handling: s.objection_handling,
  scores_engagement_rapport: s.engagement_rapport,
  scores_close_next_steps: s.close_next_steps,
  overall_score_25: obj.overall_score_25,
  overall_score_percent: obj.overall_score_percent,
  notes: obj.notes
};

return [{ json: out }];

// === Konfiguration ===
const GAP_BREAK_SECONDS = 15;     // Neuer Block, wenn zwischen SÃ¤tzen > 15s liegen
const INCLUDE_TIMESTAMPS = true;  // Zeitstempel am Block-Anfang zeigen (mm:ss)
const TIME_FORMAT = "mm:ss";      // mm:ss oder hh:mm:ss

// === Hilfsfunktionen ===
function toTimecode(sec) {
  if (sec == null || isNaN(sec)) return "";
  const s = Math.max(0, Math.floor(sec));
  const h = Math.floor(s / 3600);
  const m = Math.floor((s % 3600) / 60);
  const ss = s % 60;
  const pad = (n)=>String(n).padStart(2,"0");
  if (TIME_FORMAT === "hh:mm:ss" || h > 0) return `${pad(h)}:${pad(m)}:${pad(ss)}`;
  return `${pad(m)}:${pad(ss)}`;
}

function cleanText(t) {
  // Bewahre Original-Satz, aber trimme Leerzeichen
  return String(t ?? "").replace(/\s+/g, " ").trim();
}

function ensureEndPunctuation(t) {
  // Wenn der Block nicht auf . ! ? endet, fÃ¼ge einen Punkt hinzu
  if (!t) return t;
  return /[.!?â€¦]$/.test(t) ? t : t + ".";
}

// === Input robust finden ===
const root = items[0]?.json || {};
const data = root.data || root;
const sentences = Array.isArray(data.sentences) ? data.sentences.slice() : [];
const speakersArr = Array.isArray(data.speakers) ? data.speakers : [];

// Map Speaker-ID -> Name
const speakerMap = new Map();
for (const s of speakersArr) {
  if (s && typeof s.id !== "undefined") {
    speakerMap.set(s.id, s.name || `Speaker ${s.id}`);
  }
}

// Fallback: wenn speakerMap leer ist, holen wir Namen aus den SÃ¤tzen
if (speakerMap.size === 0) {
  for (const s of sentences) {
    if (typeof s.speaker_id !== "undefined") {
      speakerMap.set(s.speaker_id, s.speaker_name || `Speaker ${s.speaker_id}`);
    }
  }
}

// Sortieren nach index (falls vorhanden) sonst start_time
sentences.sort((a, b) => {
  if (typeof a.index === "number" && typeof b.index === "number") return a.index - b.index;
  return (a.start_time ?? 0) - (b.start_time ?? 0);
});

// === Gruppierung zu Sprecher-BlÃ¶cken ===
const blocks = []; // [{ speakerName, startTime, endTime, text }]
let current = null;

for (const s of sentences) {
  const name = speakerMap.get(s.speaker_id) || s.speaker_name || "Unbekannt";
  const t = cleanText(s.text ?? s.raw_text ?? "");

  if (!t) continue;

  const st = Number(s.start_time ?? 0);
  const et = Number(s.end_time ?? st);

  const shouldBreak =
    !current ||
    current.speakerName !== name ||
    (typeof current.lastEnd === "number" && typeof st === "number" && st - current.lastEnd > GAP_BREAK_SECONDS);

  if (shouldBreak) {
    // neuen Block starten
    current = {
      speakerName: name,
      startTime: st,
      lastEnd: et,
      texts: [t]
    };
    blocks.push(current);
  } else {
    // an bestehenden Block anhÃ¤ngen
    current.texts.push(t);
    current.lastEnd = et;
  }
}

// === Formatierung ===

// a) Formatiert mit Sprecher + Zeit (wie Beispiel)
const formattedLines = blocks.map(b => {
  let linePrefix = b.speakerName + ": ";
  if (INCLUDE_TIMESTAMPS) {
    linePrefix = `${b.speakerName}: ${toTimecode(b.startTime)}  `;
  }
  const paragraph = ensureEndPunctuation(cleanText(b.texts.join(" ")));
  return linePrefix + paragraph;
});
const transcript_formatted = formattedLines.join("\n\n");

// b) Plain-FlieÃŸtext ohne Zeitstempel, Sprecher dennoch markiert
const plainLines = blocks.map(b => {
  const paragraph = ensureEndPunctuation(cleanText(b.texts.join(" ")));
  return `${b.speakerName}: ${paragraph}`;
});
const transcript_plain = plainLines.join("\n\n");

// Optional: Metadaten (Titel/Meeting-Link), falls vorhanden
const meta = {
  title: data.title || "",
  meeting_link: data.meeting_link || "",
  organizer_email: data.organizer_email || "",
  date_iso: data.dateString || ""
};

return [{
  json: {
    transcript_formatted,
    transcript_plain,
    meta
  }
}];
