parameters.jsCode:
const candidateOutput = $input.item.json.output;

// Extract JSON from the markdown code block
// Gemini often wraps JSON in ```json ... ``` blocks
let jsonString = candidateOutput;

// Remove markdown code blocks if present
if (jsonString.includes('```json')) {
  jsonString = jsonString.replace(/```json\n?/g, '').replace(/```\n?/g, '');
}

// Parse the JSON string
let parsedData;
try {
  parsedData = JSON.parse(jsonString.trim());
} catch (error) {
  // If parsing fails, try to extract JSON between first { and last }
  const firstBrace = jsonString.indexOf('{');
  const lastBrace = jsonString.lastIndexOf('}');
  if (firstBrace !== -1 && lastBrace !== -1) {
    jsonString = jsonString.substring(firstBrace, lastBrace + 1);
    parsedData = JSON.parse(jsonString);
  } else {
    throw new Error('Failed to parse JSON from Gemini output: ' + error.message);
  }
}

// Extract key values
const result = {
  // Basic Assessment
  compatibility_score: parsedData.compatibility_score,
  recommendation: parsedData.recommendation,
  experience_years: parsedData.experience_years,
  education: parsedData.education,

  // Technical Skills (flatten for easier access)
  programming_languages: parsedData.technical_skills?.programming_languages || [],
  frameworks: parsedData.technical_skills?.frameworks || [],
  tools: parsedData.technical_skills?.tools || [],
  databases: parsedData.technical_skills?.databases || [],

  // Strengths (extract just the text)
  strengths: parsedData.key_strengths || [],
  strengths_summary: parsedData.key_strengths?.join('; ') || '',

  // Concerns (extract just the text)
  concerns: parsedData.concerns || [],
  concerns_summary: parsedData.concerns?.join('; ') || '',

  // Experience
  relevant_experience: parsedData.relevant_experience || [],
  total_roles: parsedData.relevant_experience?.length || 0,

  // Interview recommendations
  interview_focus_areas: parsedData.interview_focus_areas || [],
  interview_topics: parsedData.interview_focus_areas?.join(', ') || '',

  // Overall assessment
  overall_assessment: parsedData.overall_assessment || '',

  // Add decision flag for easy routing
  is_strong_candidate: parsedData.compatibility_score >= 7,
  is_moderate_candidate: parsedData.compatibility_score >= 5 && parsedData.compatibility_score < 7,
  is_weak_candidate: parsedData.compatibility_score < 5,

  // Original full parsed data (in case you need it)
  full_analysis: parsedData
};

return result;
