const items = $input.all();
const results = [];

for (const item of items) {
  try {
    // Get resume binary data
    const resumeData = item.binary?.data;

    if (resumeData) {
      // Convert binary to base64 for AI processing
      const base64Resume = resumeData.data;

      results.push({
        json: {
          ...item.json,
          resume_base64: base64Resume,
          resume_available: true
        }
      });
    } else {
      results.push({
        json: {
          ...item.json,
          resume_available: false,
          resume_text: 'Resume not available for parsing'
        }
      });
    }
  } catch (error) {
    results.push({
      json: {
        ...item.json,
        resume_available: false,
        resume_text: 'Error processing resume',
        error: error.message
      }
    });
  }
}

return results;

