parameters.inputSchema:
{
  "type": "object",
  "required": [
    "academic_score",
    "extracurricular_score",
    "personal_qualities_score",
    "institutional_fit_score",
    "diversity_contribution_score",
    "composite_score",
    "recommendation",
    "strengths",
    "concerns",
    "interview_recommendation",
    "interview_questions",
    "committee_discussion_points",
    "predicted_matriculation",
    "detailed_review_markdown"
  ],
  "properties": {
    "academic_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "extracurricular_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "personal_qualities_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "institutional_fit_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "diversity_contribution_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "composite_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100,
      "description": "Weighted composite score"
    },
    "recommendation": {
      "type": "string",
      "enum": ["admit", "strong_maybe", "maybe", "deny"]
    },
    "strengths": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Key strengths and standout factors"
    },
    "concerns": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Weaknesses or areas of concern"
    },
    "interview_recommendation": {
      "type": "string",
      "enum": ["must_interview", "should_interview", "optional", "skip"]
    },
    "interview_questions": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Key questions to explore in interview"
    },
    "committee_discussion_points": {
      "type": "array",
      "items": {"type": "string"}
    },
    "predicted_matriculation": {
      "type": "string",
      "enum": ["high", "medium", "low"]
    },
    "detailed_review_markdown": {
      "type": "string",
      "description": "Complete review in markdown"
    }
  }
}

