            height: 100%;
            position: relative;
            display: flex;
            flex-direction: column;
            padding: 40px 50px;
            z-index: 1;
        }

        /* Header Section */
        .header-section {
            display: flex;
            justify-content: space-between;
            align-items: flex-start;
            margin-bottom: 50px;
            width: 100%;
        }

        .logo-container {
            display: flex;
            flex-direction: column;
        }

        .logo-image {
            width: 280px;
            height: auto;
            max-height: 70px;
