            background: rgba(30, 41, 59, 0.8);
            border: 1px solid #475569;
            border-radius: 8px;
            padding: 10px 20px;
            font-size: 12px;
            font-weight: 600;
            color: #f1f5f9;
            text-transform: uppercase;
            letter-spacing: 1px;
            backdrop-filter: blur(10px);
        }

        /* Main Content Area */
        .main-content-area {
            flex: 1;
            display: flex;
            gap: 60px;
            align-items: center;
            width: 100%;
        }

        .content-left {
            flex: 1;
            max-width: 450px;
        }

        .tagline {
            font-size: 12px;
            color: #22c55e;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 1.5px;
            margin-bottom: 16px;
        }

        .main-headline {
            font-size: 56px;
            font-weight: 800;
            line-height: 0.95;
            margin-bottom: 8px;
            color: #ffffff;
        }

        .headline-accent {
            color: #22c55e;
            position: relative;
        }

        .headline-accent::after {
            content: '';
            position: absolute;
            bottom: -4px;
            left: 0;
            width: 100%;
            height: 4px;
            background: linear-gradient(90deg, #22c55e, #16a34a);
            border-radius: 2px;
        }

        .audit-subtitle {
            font-size: 56px;
            font-weight: 800;
            line-height: 0.95;
            margin-bottom: 24px;
            color: #ffffff;
        }

        .description {
            font-size: 16px;
            color: #cbd5e1;
            line-height: 1.6;
            margin-bottom: 32px;
            max-width: 420px;
        }

        .stats-row {
            display: flex;
            gap: 24px;
            margin-bottom: 20px;
        }

        .stat-item {
            display: flex;
            flex-direction: column;
            align-items: center;
            background: rgba(30, 41, 59, 0.3);
            border: 1px solid #475569;
            border-radius: 8px;
            padding: 12px 16px;
            backdrop-filter: blur(10px);
        }

        .stat-number {
            font-size: 20px;
            font-weight: 700;
            color: #22c55e;
            margin-bottom: 2px;
        }

        .stat-label {
            font-size: 11px;
            color: #94a3b8;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

