        .content-right {
            flex: 1;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 380px;
        }

        .system-container {
            width: 360px;
            height: 360px;
            position: relative;
            background: rgba(30, 41, 59, 0.4);
            border: 1px solid rgba(75, 85, 99, 0.3);
            border-radius: 16px;
            backdrop-filter: blur(10px);
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 20px;
            padding: 20px;
