        .automation-element {
            grid-column: 2;
            grid-row: 1;
            background: linear-gradient(135deg, #059669 0%, #10b981 100%);
        }

        .strategy-element {
            grid-column: 1;
            grid-row: 2;
            background: linear-gradient(135deg, #dc2626 0%, #ef4444 100%);
        }

        .optimization-element {
            grid-column: 2;
            grid-row: 2;
            background: linear-gradient(135deg, #0891b2 0%, #06b6d4 100%);
        }

        .element-title {
            font-size: 10px;
            font-weight: 500;
            opacity: 0.8;
            margin-bottom: 6px;
            letter-spacing: 0.5px;
            text-transform: uppercase;
        }

        .element-value {
            font-size: 13px;
            font-weight: 700;
            margin-bottom: 3px;
        }

        .element-metric {
            font-size: 9px;
            font-weight: 600;
            color: rgba(255, 255, 255, 0.8);
        }

        /* Clean Connection Lines */
        .connection-line {
            position: absolute;
            background: linear-gradient(45deg, #f59e0b 0%, #d97706 100%);
            height: 2px;
            border-radius: 1px;
            box-shadow: 0 0 8px rgba(245, 158, 11, 0.3);
            z-index: 15;
        }

