        .line-top-left {
            width: 50px;
            top: 140px;
            left: 110px;
            transform: rotate(45deg);
        }

        .line-top-right {
            width: 50px;
            top: 140px;
            right: 110px;
            transform: rotate(-45deg);
        }

        .line-bottom-left {
            width: 50px;
            bottom: 140px;
            left: 110px;
            transform: rotate(-45deg);
        }

        .line-bottom-right {
            width: 50px;
            bottom: 140px;
            right: 110px;
            transform: rotate(45deg);
        }

