        .floating-metric {
            position: absolute;
            background: rgba(15, 23, 42, 0.95);
            border: 1px solid #f59e0b;
            border-radius: 8px;
            padding: 4px 8px;
            font-size: 8px;
            font-weight: 700;
            color: #f59e0b;
            backdrop-filter: blur(10px);
            z-index: 25;
        }

        /* Corner Positioning for Metrics */
        .metric-1 { 
            top: 15px; 
            left: 15px; 
        }
        .metric-2 { 
            top: 15px; 
            right: 15px; 
        }
        .metric-3 { 
            bottom: 15px; 
            left: 15px; 
        }
        .metric-4 { 
            bottom: 15px; 
            right: 15px; 
        }

        /* Clean Grid Background */
        .grid-background {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-image: 
                linear-gradient(rgba(75, 85, 99, 0.05) 1px, transparent 1px),
                linear-gradient(90deg, rgba(75, 85, 99, 0.05) 1px, transparent 1px);
            background-size: 18px 18px;
            border-radius: 16px;
            opacity: 0.6;
        }

        /* Subtle Center Glow */
        .ambient-glow {
            position: absolute;
            width: 100px;
            height: 100px;
            background: radial-gradient(circle, rgba(99, 102, 241, 0.1) 0%, transparent 70%);
            border-radius: 50%;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            z-index: 1;
        }

        /* Minimal Glass Effect */
        .glass-overlay {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            height: 30%;
            background: linear-gradient(180deg, rgba(255, 255, 255, 0.04) 0%, transparent 100%);
            border-radius: 16px 16px 0 0;
            pointer-events: none;
        }

        /* Client Information */
        .client-section {
            margin-top: auto;
            background: rgba(30, 41, 59, 0.8);
            backdrop-filter: blur(15px);
            border: 1px solid #475569;
            border-radius: 16px;
            padding: 24px;
            display: flex;
            justify-content: space-between;
            align-items: center;
        }

        .client-info {
            display: flex;
            flex-direction: column;
            align-items: center;
            flex: 1;
        }

        .client-label {
            font-size: 11px;
            color: #94a3b8;
            text-transform: uppercase;
            letter-spacing: 1px;
            margin-bottom: 6px;
            font-weight: 500;
        }

        .client-name {
            font-size: 20px;
            font-weight: 700;
            color: #ffffff;
        }

        .divider {
            width: 1px;
            height: 40px;
            background: #475569;
            margin: 0 24px;
        }

        .report-info {
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        .report-date {
            font-size: 14px;
            font-weight: 600;
            color: #d97706;
        }

        /* Subtle Glow Effect */
        .glow-effect {
            position: absolute;
            width: 200px;
            height: 200px;
            background: radial-gradient(circle, rgba(217, 119, 6, 0.05) 0%, transparent 70%);
            border-radius: 50%;
            top: 40%;
            right: 30%;
            z-index: 0;
        }

        @media print {
            .page {
                margin: 0;
                box-shadow: none;
            }

            .cover-page {
                page-break-after: always;
            }
        }
    </style>
