//-------------------------------------------------------------
// Gracewell - Form Tracking Dashboard (Workflow 1)
// Author: Dr. Jeffin Gracewell ‚Äì Simplified Version
//-------------------------------------------------------------

// Step 1 ‚Äì Read data from Google Sheets
const studentSheet  = $items("Google Sheets").map(i => i.json);
const responseSheet = $items("Google Sheets1").map(i => i.json);

// Step 2 ‚Äì Normalize and Compare Register Numbers
const submittedRegs = responseSheet.map(r => String(r["Reg No"]).trim().toLowerCase());
const completed   = [];
const incompleted = [];

for (const s of studentSheet) {
  const reg = String(s["Register No."]).trim().toLowerCase();
  if (submittedRegs.includes(reg)) {
    const r = responseSheet.find(x => String(x["Reg No"]).trim().toLowerCase() === reg);
    completed.push({
      "Register No.": s["Register No."],
      "Student Name": s["Student Name"],
      "Email Address": r?.["Email Address"] || "",
      "Timestamp": r?.["Timestamp"] || "",
      "Status": "‚úÖ Completed"
    });
  } else {
    incompleted.push({
      "Register No.": s["Register No."],
      "Student Name": s["Student Name"],
      "Status": "‚ö†Ô∏è Not Submitted"
    });
  }
}

// Step 3 ‚Äì Summary
const summary = {
  total: studentSheet.length,
  submitted: completed.length,
  pending: incompleted.length,
  completion: ((completed.length / Math.max(1, studentSheet.length)) * 100).toFixed(2) + "%",
  updated: new Date().toLocaleString()
};

// Step 4 ‚Äì HTML Dashboard
const completedRows = completed
  .map(s => `<tr><td>${s["Register No."]}</td><td>${s["Student Name"]}</td><td>‚úÖ</td></tr>`)
  .join("");

const pendingRows = incompleted
  .map(s => `<tr><td>${s["Register No."]}</td><td>${s["Student Name"]}</td><td>‚ö†Ô∏è</td></tr>`)
  .join("");

const html = `
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Gracewell ‚Äì Form Tracking Dashboard</title>
<script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>
<style>
body{font-family:'Segoe UI',Arial;background:#f9fafb;text-align:center;padding:30px;}
h1{color:#2563eb;margin-bottom:10px;}
table{border-collapse:collapse;margin:15px auto;width:90%;max-width:900px;background:#fff;
      box-shadow:0 2px 6px rgba(0,0,0,0.1);}
th,td{padding:8px 12px;border-bottom:1px solid #eee;text-align:left;}
th{background:#eff6ff;color:#1e3a8a;}
button{margin:10px 8px;padding:10px 22px;border:none;border-radius:6px;font-size:15px;cursor:pointer;}
.send{background:#dc2626;color:#fff;}
.ack{background:#16a34a;color:#fff;}
footer{margin-top:25px;color:#666;font-size:14px;}
</style>
</head>
<body>

<h1>üìä Gracewell Form Tracking Dashboard</h1>
<p>Total <strong>${summary.total}</strong> | Submitted <strong>${summary.submitted}</strong> | Pending <strong>${summary.pending}</strong> | Completion <strong>${summary.completion}</strong></p>

<button class="ack"  onclick="acknowledge()">‚úÖ Send Acknowledgements</button>
<button class="send" onclick="remind()">‚ö†Ô∏è Send Reminders</button>

<section>
  <h2 style="color:#16a34a;">‚úÖ Completed (${completed.length})</h2>
  <table><tr><th>Reg No</th><th>Student Name</th><th>Status</th></tr>${completedRows}</table>
</section>

<section>
  <h2 style="color:#dc2626;">‚ö†Ô∏è Pending (${incompleted.length})</h2>
  <table><tr><th>Reg No</th><th>Student Name</th><th>Status</th></tr>${pendingRows}</table>
</section>

<footer>üïí Last Updated: ${summary.updated}</footer>

<script>
function remind(){
  Swal.fire({
    title:'Send Reminders?',
    text:'${incompleted.length} pending students will receive reminder emails or WhatsApp messages.',
    icon:'question',showCancelButton:true,
    confirmButtonText:'Yes, Send'
  }).then(r=>{
    if(r.isConfirmed){
      window.location.href='https://n8n.srv765546.hstgr.cloud/webhook/send-reminder';
    }
  });
}
function acknowledge(){
  Swal.fire({
    title:'Send Acknowledgements?',
    text:'${completed.length} students will receive thank-you messages.',
    icon:'question',showCancelButton:true,
    confirmButtonText:'Yes, Send'
  }).then(r=>{
    if(r.isConfirmed){
      window.location.href='https://n8n.srv765546.hstgr.cloud/webhook/send-acknowledgements';
    }
  });
}
</script>

</body>
</html>
`;

// Step 5 ‚Äì Return
return [{
  json: { summary, completed, incompleted, html }
}];

//-------------------------------------------------------------
// Gracewell Workflow 2: Send Acknowledgements (Enhanced & Stable)
//-------------------------------------------------------------

// --- Read data from both Sheets nodes ---
const students  = $items('Google Sheets - Student List').map(i => i.json);
const responses = $items('Google Sheets FormResponses').map(i => i.json);

// --- Normalization helper ---
const normalize = v => (v ? String(v).trim().toLowerCase() : '');

// --- Lists ---
const completed = [];

// --- Match Register Numbers ---
for (const s of students) {
  const reg = normalize(s['Register No.']);
  const match = responses.find(r => normalize(r['Reg No']) === reg);

  if (match) {
    completed.push({
      'Register No.': s['Register No.'],
      'Student Name': s['Student Name'],
      'Contact': s['Contact'] || '',               // ‚úÖ added
      'Email Address': s['Email'] || match['Email Address'] || '',
      'Timestamp': match['Timestamp'] || '',
      'Status': '‚úÖ Completed'
    });
  }
}

// --- Prepare Email Content ---
const subject = '‚úÖ Thank You for Your Submission';
const bodyTemplate = `
Dear {{name}},<br><br>
Thank you for completing your form (Reg No: {{reg}}).<br>
We appreciate your prompt response.<br><br>
‚Äì Gracewell Technologies Team
`;

// --- Helper for placeholder replacement ---
function fill(template, s) {
  return template
    .replace(/\{\{\s*name\s*\}\}/gi, s['Student Name'])
    .replace(/\{\{\s*reg\s*\}\}/gi, s['Register No.']);
}

// --- Handle case of no completed students ---
if (completed.length === 0) {
  return [{ json: { message: 'No completed students found' } }];
}

// --- Output: one JSON per student ---
return completed.map(s => ({
  json: {
    'Register No.': s['Register No.'],
    'Student Name': s['Student Name'],
    'Contact': s['Contact'],
    'Email Address': s['Email Address'],
    'subject': subject,
    'htmlBody': fill(bodyTemplate, s)
  }
}));

//-------------------------------------------------------------
// Gracewell Workflow 3: Send Reminders (Pending Students)
//-------------------------------------------------------------

// --- Read data from both Sheets nodes ---
const students  = $items('Google Sheets - Student List1').map(i => i.json);
const responses = $items('Google Sheets - FormResponses').map(i => i.json);

// --- Helper function to normalize Register Numbers ---
const normalize = v => (v ? String(v).trim().toLowerCase() : '');

// --- Extract submitted register numbers ---
const submittedRegs = responses.map(r => normalize(r['Reg No']));

// --- Identify pending students ---
const pending = [];

for (const s of students) {
  const reg = normalize(s['Register No.']);
  if (!submittedRegs.includes(reg)) {
    pending.push({
      'Register No.': s['Register No.'],
      'Student Name': s['Student Name'],
      'Contact': s['Contact'] || '',
      'Email Address': s['Email'] || '',
      'Status': '‚ö†Ô∏è Not Submitted'
    });
  }
}

// --- Stop execution if no pending students ---
if (pending.length === 0) {
  return [{ json: { message: 'All students have submitted. No reminders needed.' } }];
}

// --- Define email subject & body template ---
const subject = '‚ö†Ô∏è Reminder: Please Complete Your Form';
const bodyTemplate = `
Dear {{name}},<br><br>
Our records show that you have not yet completed the required form (Reg No: {{reg}}).<br>
Kindly fill it at your earliest convenience.<br><br>
‚Äì Gracewell AI
`;

// --- Helper to replace placeholders in the message ---
function fill(template, s) {
  return template
    .replace(/\{\{\s*name\s*\}\}/gi, s['Student Name'])
    .replace(/\{\{\s*reg\s*\}\}/gi, s['Register No.']);
}

// --- Prepare final output for each pending student ---
return pending.map(s => ({
  json: {
    'Register No.': s['Register No.'],
    'Student Name': s['Student Name'],
    'Contact': s['Contact'],
    'Email Address': s['Email Address'],
    'subject': subject,
    'htmlBody': fill(bodyTemplate, s)
  }
}));
