// Merge AI agent output with client data
const data = $input.first().json;
let aiOut;
try {
  aiOut = JSON.parse(data.output);
} catch (e) {
  aiOut = {
    roles: ['Project Manager','Developer','Designer'],
    estimatedHours: 200,
    isBudgetSufficient: false,
    priority: 'medium',
    justification: 'Fallback values used'
  };
}

return {
  json: {
    ...data,
    suggestedTeam: aiOut.roles,
    estimatedHours: aiOut.estimatedHours,
    isBudgetSufficient: aiOut.isBudgetSufficient,
    priority: aiOut.priority,
    teamSize: aiOut.roles.length,
    hourlyRate: Math.round(data.budget / aiOut.estimatedHours),
    aiAnalysis: aiOut.justification
  }
};

// Generate project tasks based on project type and timeline
const data = $input.first().json;

const standardTasks = [
  { name: 'Kickoff Meeting', assignee: 'Project Manager', dueOffset: 1 },
  { name: 'Gather Requirements', assignee: 'Project Manager', dueOffset: 3 },
  { name: 'Create Project Brief', assignee: 'Project Manager', dueOffset: 5 },
  { name: 'Design Phase - Wireframes', assignee: 'UI/UX Designer', dueOffset: 10 },
  { name: 'Design Phase - Visual Design', assignee: 'UI/UX Designer', dueOffset: 15 },
  { name: 'Client Review - Design', assignee: 'Project Manager', dueOffset: 18 },
  { name: 'Development Phase Start', assignee: 'Lead Developer', dueOffset: 21 },
  { name: 'Mid-Project Check-in', assignee: 'Project Manager', dueOffset: 35 },
  { name: 'QA Testing', assignee: 'QA Tester', dueOffset: 50 },
  { name: 'Client UAT', assignee: 'Project Manager', dueOffset: 55 },
  { name: 'Final Revisions', assignee: 'Team', dueOffset: 60 },
  { name: 'Launch/Delivery', assignee: 'Project Manager', dueOffset: 65 },
  { name: 'Post-Launch Review', assignee: 'Project Manager', dueOffset: 70 }
];

const tasks = standardTasks.map(task => {
  const dueDate = new Date(data.startDate);
  dueDate.setDate(dueDate.getDate() + task.dueOffset);

  return {
    name: task.name,
    assignee: task.assignee,
    dueDate: dueDate.toISOString().split('T')[0],
    description: `Task for ${data.projectName}`,
    priority: task.dueOffset < 10 ? 'high' : 'normal'
  };
});

return {
  json: {
    ...data,
    tasks: tasks,
    taskCount: tasks.length
  }
};

