// --- Ultra-Resilient Gemini Blog Cleaner ---
// Works with Markdown fences, nested JSON, or plain text

const raw = $json.content?.parts?.[0]?.text || '';
if (!raw) throw new Error('Gemini returned empty response.');

let text = raw.trim();

// 1Ô∏è‚É£ Strip Markdown fences and language hints
text = text
  .replace(/```json/gi, '')
  .replace(/```/g, '')
  .replace(/^#+\s+/gm, '')
  .trim();

// 2Ô∏è‚É£ Find the innermost JSON block
let matches = text.match(/\{[\s\S]*\}/g);
let parsed = {};

if (matches && matches.length) {
  // pick the deepest block (most nested)
  let jsonText = matches[matches.length - 1]
    .replace(/‚Äú|‚Äù/g, '"')
    .replace(/‚Äò|‚Äô/g, "'")
    .replace(/,\s*([}\]])/g, '$1')
    .replace(/'/g, '"')
    .trim();

  try {
    parsed = JSON.parse(jsonText);
  } catch (err) {
    // attempt repair if invalid JSON
    let repaired = jsonText.replace(/([{,]\s*)([A-Za-z0-9_]+)\s*:/g, '$1"$2":');
    try {
      parsed = JSON.parse(repaired);
    } catch (err2) {
      parsed = {};
    }
  }
}

// 3Ô∏è‚É£ Regex fallback if still not parsed
if (!parsed.title) {
  parsed.title = text.match(/"title"\s*:\s*"([^"]+)"/i)?.[1]
    || text.match(/title\s*[:\-]\s*(.+)/i)?.[1]?.trim()
    || '';
}
if (!parsed.slug) {
  parsed.slug = text.match(/"slug"\s*:\s*"([^"]+)"/i)?.[1]
    || text.match(/slug\s*[:\-]\s*(.+)/i)?.[1]?.trim()
    || '';
}
if (!parsed.excerpt) {
  parsed.excerpt = text.match(/"excerpt"\s*:\s*"([^"]+)"/i)?.[1]
    || text.match(/excerpt\s*[:\-]\s*(.+)/i)?.[1]?.trim()
    || '';
}
if (!parsed.content) {
  const contentMatch = text.match(/"content"\s*:\s*"([\s\S]+)"/i);
  parsed.content = contentMatch
    ? contentMatch[1]
    : text.match(/content\s*[:\-]\s*([\s\S]+)/i)?.[1]?.trim()
    || '';
}
if (!parsed.keywords) {
  parsed.keywords = text.match(/"keywords"\s*:\s*"([^"]+)"/i)?.[1]
    || text.match(/keywords?\s*[:\-]\s*(.+)/i)?.[1]?.trim()
    || '';
}
if (!parsed.image_prompt) {
  parsed.image_prompt = text.match(/"image_prompt"\s*:\s*"([^"]+)"/i)?.[1]
    || text.match(/image[_\s]?prompt\s*[:\-]\s*(.+)/i)?.[1]?.trim()
    || '';
}

// 4Ô∏è‚É£ Normalize and sanitize output
parsed.title = parsed.title?.replace(/^["']|["']$/g, '') || 'Untitled Blog';
parsed.slug =
  parsed.slug ||
  parsed.title.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-+|-+$/g, '');
parsed.excerpt = parsed.excerpt?.replace(/^["']|["']$/g, '') || parsed.content.slice(0, 160);
parsed.content = parsed.content?.replace(/^["']|["']$/g, '') || '';
parsed.keywords = parsed.keywords?.replace(/^["']|["']$/g, '') || '';
parsed.image_prompt =
  parsed.image_prompt?.replace(/^["']|["']$/g, '') || parsed.title;

// 5Ô∏è‚É£ Output ready-to-use normalized data
return [parsed];
