// n8n Code node: merge all incoming items into a single output array in one item

const inputItems = $input.all();
const combined = [];

// Normalize any value to an array of plain objects
function normalizeToArray(val) {
  if (typeof val === 'string') {
    const trimmed = val.trim();
    try {
      const parsed = JSON.parse(trimmed);
      return Array.isArray(parsed) ? parsed : [parsed];
    } catch {
      return [{ raw: trimmed }];
    }
  }
  if (Array.isArray(val)) return val;
  if (val && typeof val === 'object') return [val];
  return [{ value: val }];
}

// Merge all items' json into one array
for (const item of inputItems) {
  const source = item.json !== undefined ? item.json : item;
  const arr = normalizeToArray(source);
  for (const el of arr) {
    const obj = el && typeof el === 'object' ? el : { value: el };
    combined.push(obj);
  }
}

// Return a single n8n item containing the whole array
return [
  {
    json: {
      combined,            // All input collapsed into one array
      count: combined.length
    }
  }
];

