// filename: n8n-generate-weekly-summary-email.js

/**
 * n8n Code node (JavaScript) – Fixes “Code doesn't return items properly”
 * and returns a valid array of items with the generated HTML email.
 *
 * Input expectation:
 * - Each incoming item has a field `output` (string) containing the weekly summary text
 *   with sections 1) .. 8) as provided by your AI Agent node.
 *
 * Output:
 * - Returns an array with ONE item:
 *     { json: { html: "<!-- full HTML email -->", subject: "Weekly Summary", plainTextPreview: "..." } }
 *
 * If multiple input items exist, it uses the first one with `output` present.
 *
 * Place this code in your “Code” node and ensure the previous node passes an item
 * with `output` in `item.json.output`.
 */

// --------------- Helper functions ---------------

// Basic HTML escaping
function escapeHtml(str) {
  return String(str)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;');
}

// Convert "1) **Weekly Overview**" -> "Weekly Overview"
function cleanHeading(headingText) {
  let h = headingText.replace(/^\d\)\s*/, '');
  h = h.replace(/\*\*/g, '').trim();
  return h;
}

// Inline formatting: bold **text**, issue keys as pills
function inlineFormat(text) {
  let t = escapeHtml(text);
  t = t.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
  t = t.replace(/\b([A-Z]+-\d+)\b/g, '<span class="pill">$1</span>');
  return t;
}

// Render section content: bullets, paragraphs, snapshot code blocks
function renderSectionContent(content) {
  if (!content) return '<p class="muted">None</p>';

  const lines = content.split('\n');
  const blocks = [];
  let currentList = null;

  const flushList = () => {
    if (currentList && currentList.items.length) {
      const li = currentList.items.map(i => `<li>${inlineFormat(i)}</li>`).join('');
      blocks.push(`<ul class="metric-list">${li}</ul>`);
    }
    currentList = null;
  };

  for (let rawLine of lines) {
    const line = rawLine.trim();

    if (line.length === 0) {
      flushList();
      continue;
    }
    if (/^-\s+/.test(line)) {
      if (!currentList) currentList = { items: [] };
      currentList.items.push(line.replace(/^-\s+/, '').trim());
      continue;
    }
    flushList();

    // JSON-like snapshot lines -> code block
    if (/^\{.+\}$/.test(line) || line.startsWith('{ "') || line.startsWith('- {')) {
      const cleaned = line.replace(/^-+\s*/, '');
      blocks.push(`<div class="code">${escapeHtml(cleaned)}</div>`);
    } else {
      blocks.push(`<p>${inlineFormat(line)}</p>`);
    }
  }

  flushList();
  return blocks.join('\n');
}

// Build email HTML from the `output` string
function buildEmailHtml(outputText) {
  // Find section headings like "1) **Weekly Overview**"
  const headingRegex = /\n?\s*(\d\)\s\*\*?[^\n]+?\*\*?\s*)/g;
  const parts = [];
  let match;

  while ((match = headingRegex.exec(outputText)) !== null) {
    const headingText = match[1];
    const startIndexForContent = headingRegex.lastIndex;
    const cleanedHeading = cleanHeading(headingText);

    // Set previous section content boundary
    if (parts.length > 0) {
      parts[parts.length - 1].content = outputText.slice(parts[parts.length - 1].startIndex, match.index).trim();
    }

    parts.push({
      heading: cleanedHeading,
      startIndex: startIndexForContent,
      content: '',
    });
  }

  if (parts.length > 0) {
    const last = parts[parts.length - 1];
    last.content = outputText.slice(last.startIndex).trim();
  } else {
    // Fallback: whole text as one section
    parts.push({
      heading: 'Weekly Summary',
      content: outputText.trim(),
    });
  }

  const sectionHtml = parts.map(({ heading, content }) => {
    return `
      <section class="card">
        <h2 class="section-title">${escapeHtml(heading)}</h2>
        ${renderSectionContent(content)}
      </section>
    `;
  }).join('\n');

  const generatedAtIst = escapeHtml(new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' }));

  const html = `
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8" />
<meta name="viewport" content="width=device-width, initial-scale=1" />
<title>Weekly Summary</title>
<style>
  :root {
    --bg: #f7f8fa;
    --card-bg: #ffffff;
    --text: #111827;
    --muted: #6b7280;
    --primary: #2563eb;
    --border: #e5e7eb;
  }
  body {
    margin: 0;
    background: var(--bg);
    color: var(--text);
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Inter, Oxygen, Ubuntu, Cantarell, "Fira Sans", "Droid Sans", "Helvetica Neue", Arial, sans-serif;
    line-height: 1.6;
  }
  .container {
    max-width: 760px;
    margin: 0 auto;
    padding: 24px 16px;
  }
  .header {
    background: linear-gradient(135deg, #eef2ff, #f0fdf4);
    border: 1px solid var(--border);
    border-radius: 14px;
    padding: 20px;
    margin-bottom: 16px;
  }
  .title {
    margin: 0;
    font-size: 22px;
    font-weight: 700;
  }
  .subtitle {
    margin: 6px 0 0;
    font-size: 14px;
    color: var(--muted);
  }
  .card {
    background: var(--card-bg);
    border: 1px solid var(--border);
    border-radius: 12px;
    padding: 18px;
    margin: 12px 0;
    box-shadow: 0 2px 6px rgba(31, 41, 55, 0.06);
  }
  .section-title {
    margin: 0 0 12px;
    font-size: 18px;
    font-weight: 700;
    color: var(--text);
    border-left: 3px solid var(--primary);
    padding-left: 10px;
  }
  p { margin: 10px 0; }
  ul.metric-list { padding-left: 18px; margin: 8px 0; }
  ul.metric-list li { margin: 6px 0; }
  .pill {
    display: inline-block;
    font-size: 12px;
    padding: 4px 8px;
    border-radius: 999px;
    background: #eef2ff;
    color: #1e3a8a;
    border: 1px solid #e5e7eb;
    margin-right: 6px;
  }
  .code {
    font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
    font-size: 12px;
    background: #f3f4f6;
    border: 1px solid var(--border);
    border-radius: 8px;
    padding: 8px;
    white-space: pre-wrap;
    word-break: break-word;
    margin: 8px 0;
  }
  .footer {
    text-align: center;
    color: var(--muted);
    font-size: 12px;
    margin-top: 18px;
  }
  @media (max-width: 480px) {
    .container { padding: 18px 12px; }
    .title { font-size: 20px; }
    .section-title { font-size: 17px; }
  }
</style>
</head>
<body>
  <div class="container">
    <header class="header">
      <h1 class="title">Weekly Summary</h1>
      <p class="subtitle">Generated automatically from your weekly report data.</p>
    </header>

    ${sectionHtml}

    <footer class="footer">
      Sent via automated workflow · ${generatedAtIst} IST
    </footer>
  </div>
</body>
</html>
  `.trim();

  return html;
}

// Create a short plain text preview (for email providers that show a preview line)
function buildPlainPreview(outputText) {
  // Take the first line after Weekly Overview for a preview
  const firstLine = outputText.split('\n').find(l => l.trim().length > 0) || 'Weekly Summary';
  return firstLine.replace(/\*\*/g, '').replace(/\s+/g, ' ').trim().slice(0, 160);
}

// --------------- Main n8n code ---------------

const items = $input.all();

// Pick the first item that has `output`
let outputText = null;
for (const it of items) {
  if (it && it.json && typeof it.json.output === 'string' && it.json.output.trim().length > 0) {
    outputText = it.json.output;
    break;
  }
}

// If not found, try the first item's whole JSON converted to string
if (!outputText) {
  if (items.length > 0 && items[0].json) {
    outputText = JSON.stringify(items[0].json);
  } else {
    outputText = 'Weekly Summary\n\nNo output text found in incoming items.';
  }
}

// Build HTML and preview
const html = buildEmailHtml(outputText);
const preview = buildPlainPreview(outputText);

// Return one n8n item with the email payload
return [
  {
    json: {
      subject: 'Weekly Summary',
      html,
      plainTextPreview: preview,
    },
  },
];
