parameters.functionCode:
const items = [];

for (const item of $input.all()) {
  const issue = item.json;
  const issueKey = issue.key;
  const summary = issue.fields.summary;
  const dueDate = issue.fields.duedate || 'No due date';
  const assignee = issue.fields.assignee?.displayName || 'Unassigned';
  const assigneeEmail = issue.fields.assignee?.emailAddress || '';
  const issueLinks = issue.fields.issuelinks || [];

  // Process each issue link
  for (const link of issueLinks) {
    const linkTypeName = link.type.name;
    let dependencyType = '';
    let linkedIssueKey = '';
    let linkedIssueSummary = '';
    let linkedIssueStatus = '';
    let linkedIssuePriority = '';

    // Check if this issue is blocked by another issue (inwardIssue)
    if (link.inwardIssue) {
      dependencyType = link.type.inward; // "is blocked by"
      linkedIssueKey = link.inwardIssue.key;
      linkedIssueSummary = link.inwardIssue.fields?.summary || '';
      linkedIssueStatus = link.inwardIssue.fields?.status?.name || '';
      linkedIssuePriority = link.inwardIssue.fields?.priority?.name || '';
    }
    // Check if this issue blocks another issue (outwardIssue)
    else if (link.outwardIssue) {
      dependencyType = link.type.outward; // "blocks"
      linkedIssueKey = link.outwardIssue.key;
      linkedIssueSummary = link.outwardIssue.fields?.summary || '';
      linkedIssueStatus = link.outwardIssue.fields?.status?.name || '';
      linkedIssuePriority = link.outwardIssue.fields?.priority?.name || '';
    }

    // Only add items where we found a linked issue
    if (linkedIssueKey) {
      items.push({
        json: {
          issueKey,
          summary,
          dueDate,
          assignee,
          assigneeEmail,
          linkType: linkTypeName,
          dependencyType,
          linkedIssueKey,
          linkedIssueSummary,
          linkedIssueStatus,
          linkedIssuePriority
        }
      });
    }
  }
}

return items;

const items = [];

for (const item of $input.all()) {
  const jiraData = item.json;
  const issueKey = jiraData.issueKey;
  const dueDate = jiraData.dueDate;
  const linkedIssueKey = jiraData.linkedIssueKey;

  let daysTillDue = 999;
  let riskFlag = 'Low';

  if (dueDate && dueDate !== 'No due date') {
    const today = new Date();
    const due = new Date(dueDate);
    daysTillDue = Math.ceil((due - today) / (1000 * 60 * 60 * 24));

    if (daysTillDue < 7) {
      riskFlag = 'High';
    } else if (daysTillDue < 14) {
      riskFlag = 'Medium';
    }
  }

  const blockerCount = jiraData.dependencyType === 'is blocked by' ? 1 : 0.5;
  const impactScore = Math.round(blockerCount * Math.max(1, 30 - daysTillDue));

  items.push({
    json: {
      ...jiraData,
      daysTillDue,
      riskFlag,
      impactScore
    }
  });
}

return items;

