// Parse inspection data and determine compliance status
const item = $input.item.json;

// Extract inspection details
const inspectionData = {
  id: item.inspection_id || new Date().getTime().toString(),
  timestamp: new Date().toISOString(),
  product_name: item.product_name || item.answers?.product_name || '',
  batch_number: item.batch_number || item.answers?.batch_number || '',
  inspector_name: item.inspector_name || item.answers?.inspector_name || '',
  inspection_type: item.inspection_type || item.answers?.inspection_type || 'Standard',

  // Measurement data
  measurements: {
    dimension_length: parseFloat(item.dimension_length || item.answers?.dimension_length || 0),
    dimension_width: parseFloat(item.dimension_width || item.answers?.dimension_width || 0),
    dimension_height: parseFloat(item.dimension_height || item.answers?.dimension_height || 0),
    weight: parseFloat(item.weight || item.answers?.weight || 0),
    temperature: parseFloat(item.temperature || item.answers?.temperature || 0)
  },

  // Quality checks
  visual_inspection: item.visual_inspection || item.answers?.visual_inspection || 'Pass',
  surface_finish: item.surface_finish || item.answers?.surface_finish || 'Acceptable',
  color_match: item.color_match || item.answers?.color_match || 'Yes',
  defects_found: item.defects_found || item.answers?.defects_found || 'None',

  // Compliance calculation
  specifications: {
    length_min: 100,
    length_max: 102,
    width_min: 50,
    width_max: 52,
    height_min: 25,
    height_max: 27,
    weight_min: 450,
    weight_max: 550
  }
};

// Check compliance
const m = inspectionData.measurements;
const s = inspectionData.specifications;

const dimensionsCompliant = 
  m.dimension_length >= s.length_min && m.dimension_length <= s.length_max &&
  m.dimension_width >= s.width_min && m.dimension_width <= s.width_max &&
  m.dimension_height >= s.height_min && m.dimension_height <= s.height_max;

const weightCompliant = m.weight >= s.weight_min && m.weight <= s.weight_max;

const visualCompliant = 
  inspectionData.visual_inspection === 'Pass' &&
  inspectionData.surface_finish === 'Acceptable' &&
  inspectionData.color_match === 'Yes';

inspectionData.compliance_status = 
  dimensionsCompliant && weightCompliant && visualCompliant ? 'PASS' : 'FAIL';

inspectionData.non_conformities = [];
if (!dimensionsCompliant) inspectionData.non_conformities.push('Dimensions out of spec');
if (!weightCompliant) inspectionData.non_conformities.push('Weight out of spec');
if (!visualCompliant) inspectionData.non_conformities.push('Visual inspection failed');

return inspectionData;

