// Generate daily compliance summary
const items = $input.all();
const today = new Date().toDateString();

const todaysInspections = items.filter(item => {
  const itemDate = new Date(item.json.timestamp).toDateString();
  return itemDate === today;
});

const summary = {
  date: today,
  total_inspections: todaysInspections.length,
  passed: todaysInspections.filter(i => i.json.compliance_status === 'PASS').length,
  failed: todaysInspections.filter(i => i.json.compliance_status === 'FAIL').length,
  pass_rate: 0,
  products_inspected: [...new Set(todaysInspections.map(i => i.json.product_name))],
  inspectors: [...new Set(todaysInspections.map(i => i.json.inspector_name))],
  common_issues: {}
};

if (summary.total_inspections > 0) {
  summary.pass_rate = ((summary.passed / summary.total_inspections) * 100).toFixed(1);
}

// Count non-conformities
todaysInspections.forEach(inspection => {
  if (inspection.json.non_conformities) {
    inspection.json.non_conformities.forEach(issue => {
      summary.common_issues[issue] = (summary.common_issues[issue] || 0) + 1;
    });
  }
});

return summary;

