parameters.jsCode:
// Transform the AI agent output for looping
if ($json.output.match_type === "cv_match") {
  // Return each JD as a separate item for the loop
  return $json.output.cv_match.map(jd => ({
    jd_filename: jd.jd_filename,
    jd_file_id: jd.jd_file_id
  }));
} else {
  // For email match, return single item
  return [{
    jd_filename: $json.output.email_match.jd_filename,
    jd_file_id: $json.output.email_match.jd_file_id
  }];
}

// Get the selected filename from the AI agent output
const selectedFilename = $json.output.selected_jd.jd_filename;

// Get all the JD data from the loop output
const allJDs = $('Loop Over Items').all();

// Find the matching JD by filename
const selectedJD = allJDs.find(jd => jd.json.jd_filename === selectedFilename);

if (!selectedJD) {
  throw new Error(`No JD found with filename: ${selectedFilename}`);
}

// Return just the selected JD data
return [{
  selected_jd_filename: selectedJD.json.jd_filename,
  selected_jd_file_id: selectedJD.json.jd_file_id,
  selected_jd_text: selectedJD.json.text
}];

