parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "match_type": {
      "type": "string",
      "enum": ["email_match", "cv_match"],
      "description": "Whether the match was found in email or requires CV analysis"
    },
    "email_match": {
      "type": "object",
      "properties": {
        "jd_filename": {
          "type": "string",
          "description": "Exact filename of the matched JD (PDF or Google Doc)"
        },
        "jd_file_id": {
          "type": "string",
          "description": "Google Drive file ID for downloading the JD file"
        },
        "confidence": {
          "type": "string",
          "enum": ["high", "medium", "low"],
          "description": "Confidence level of the email-based match"
        }
      },
      "required": ["jd_filename", "jd_file_id", "confidence"]
    },
    "cv_match": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "jd_filename": {
            "type": "string",
            "description": "JD filename to download and analyze (PDF or Google Doc)"
          },
          "jd_file_id": {
            "type": "string",
            "description": "Google Drive file ID for downloading the JD file"
          }
        },
        "required": ["jd_filename", "jd_file_id"]
      },
      "minItems": 1,
      "maxItems": 3,
      "description": "Up to a maximum of 3 JD files for detailed analysis, but 1-2 JD files is also fine if you do not think there are 3 JD files relevant"
    }
  },
  "required": ["match_type"],
  "oneOf": [
    {
      "properties": {
        "match_type": {"const": "email_match"}
      },
      "required": ["email_match"]
    },
    {
      "properties": {
        "match_type": {"const": "cv_match"}  
      },
      "required": ["cv_match"]
    }
  ]
}

