parameters.jsCode:
// This new code outputs ONE item with all the data combined.

const aiNodeOutput = items[0].json;
const rawText = aiNodeOutput.content.parts[0].text;
const cleanedJsonString = rawText.replace(/^```json\n/, '').replace(/\n```$/, '').trim();
const parsedData = JSON.parse(cleanedJsonString);

// Return a single item containing the post_text, image_headline, and style_used
return [{
  json: parsedData
}];

