const items = $input.all();
const results = [];

for (const item of items) {
  const html = item.json.data;
  const jobData = item.json;

  let description = '';
  const descMatch = html.match(/class="show-more-less-html__markup[^>]*>([\s\S]*?)<\/div>/);
  if (descMatch) {
    description = descMatch[1].replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
    // Truncate to 2000 characters max for Notion
    if (description.length > 2000) {
      description = description.substring(0, 1997) + '...';
    }
  }

  let posterName = '';
  const posterMatch = html.match(/class="hiring-team__member-name[^>]*>\s*([^<]+)</);
  if (posterMatch) {
    posterName = posterMatch[1].trim();
  }

  let posterTitle = '';
  const titleMatch = html.match(/class="hiring-team__member-title[^>]*>\s*([^<]+)</);
  if (titleMatch) {
    posterTitle = titleMatch[1].trim();
  }

  let posterProfile = '';
  const profileMatch = html.match(/href="(\/in\/[^"]+)"[^>]*class="hiring-team/);
  if (profileMatch) {
    posterProfile = 'https://www.linkedin.com' + profileMatch[1];
  }

  results.push({
    json: {
      ...jobData,
      description: description,
      poster_name: posterName || 'Not found',
      poster_title: posterTitle || 'Not found',
      poster_profile: posterProfile || 'Not found'
    }
  });
}

return results;

const html = $input.all()[0].json.data;
const jobCards = [];
const jobLinkRegex = /href="(https:\/\/www\.linkedin\.com\/jobs\/view\/[^"]+)"/g;
const jobLinks = [];
let match;

while ((match = jobLinkRegex.exec(html)) !== null) {
  jobLinks.push(match[1]);
}

for (let i = 0; i < jobLinks.length; i++) {
  const jobUrl = jobLinks[i];
  const urlIndex = html.indexOf(jobUrl);
  if (urlIndex === -1) continue;

  const startIndex = Math.max(0, urlIndex - 3000);
  const endIndex = Math.min(html.length, urlIndex + 1000);
  const cardSection = html.substring(startIndex, endIndex);

  // Extract title
  let title = '';
  const titleMatch = cardSection.match(/class="base-search-card__title[^>]*>\s*([^<]+)</i);
  if (titleMatch) {
    title = titleMatch[1].trim().replace(/\s+/g, ' ');
  }

  // Extract company - try multiple patterns
  let company = '';

  // Pattern 1: hidden-nested-link (most common)
  const companyMatch1 = cardSection.match(/class="hidden-nested-link[^>]*>\s*([^<]+)</i);
  if (companyMatch1) {
    company = companyMatch1[1].trim();
  }

  // Pattern 2: base-search-card__subtitle
  if (!company) {
    const companyMatch2 = cardSection.match(/class="base-search-card__subtitle[^>]*>\s*([^<]+)</i);
    if (companyMatch2) {
      company = companyMatch2[1].trim();
    }
  }

  // Pattern 3: Look for company name in any link before the job URL
  if (!company) {
    const companyMatch3 = cardSection.match(/href="\/company\/[^"]*"[^>]*>\s*([^<]+)</i);
    if (companyMatch3) {
      company = companyMatch3[1].trim();
    }
  }

  // Extract location
  let location = '';
  const locationMatch = cardSection.match(/class="job-search-card__location[^>]*>\s*([^<]+)</i);
  if (locationMatch) {
    location = locationMatch[1].trim();
  }

  // Extract posted date
  let posted = '';
  const dateMatch = cardSection.match(/<time[^>]*>\s*([^<]+)</i);
  if (dateMatch) {
    posted = dateMatch[1].trim();
  }

  jobCards.push({
    json: {
      job_title: title || 'Title not found',
      company: company || 'Company not found',
      location: location || 'Location not specified',
      job_url: jobUrl,
      posted_date: posted || 'Date not specified',
      source: 'linkedin'
    }
  });
}

return jobCards;

const items = $input.all();
const results = [];

for (const item of items) {
  const tweet = item.json;
  const text = tweet.text || '';
  const author = tweet.includes?.users?.[0] || {};

  // Skip if tweet contains excluded keywords
  const excludedWords = ['contract', 'freelance', 'junior', 'intern', 'c2c', 'corp-to-corp'];
  const textLower = text.toLowerCase();
  if (excludedWords.some(word => textLower.includes(word))) {
    continue;
  }

  // Extract job title
  let jobTitle = '';
  const titlePatterns = [
    /(senior\s+product\s+designer)/i,
    /(product\s+design\s+lead)/i,
    /(lead\s+product\s+designer)/i,
    /(senior\s+ux\s+designer)/i,
    /(principal\s+(?:product\s+)?designer)/i,
    /(staff\s+(?:product\s+)?designer)/i
  ];

  for (let pattern of titlePatterns) {
    const match = text.match(pattern);
    if (match) {
      jobTitle = match[1].trim();
      break;
    }
  }

  // Extract company
  let company = '';
  const companyPatterns = [
    /@([A-Z][a-zA-Z0-9_]+)\s+(?:is|are)\s+(?:hiring|looking)/,
    /(?:at|join|@)\s+([A-Z][a-zA-Z0-9]+?)(?:\s+(?:is|are|we|-))/,
    /([A-Z][a-zA-Z0-9\s]{2,30})\s+(?:is|are)\s+(?:hiring|looking|seeking)/
  ];

  for (let pattern of companyPatterns) {
    const match = text.match(pattern);
    if (match) {
      company = match[1].trim().replace(/^@/, '');
      break;
    }
  }

  // If no company found, use author name or check bio
  if (!company) {
    if (author.description) {
      const bioMatch = author.description.match(/(?:@|at)\s+([A-Z][a-zA-Z0-9]+)/);
      if (bioMatch) company = bioMatch[1];
    }
    if (!company) company = author.name || 'See tweet';
  }

  // Extract location with priority for SF and Remote
  let location = '';
  const sfPatterns = [
    /san francisco|SF bay area|bay area/i,
    /remote|fully remote|work from anywhere|work from home|WFH/i,
    /hybrid.*(?:san francisco|SF)/i
  ];

  for (let pattern of sfPatterns) {
    const match = text.match(pattern);
    if (match) {
      location = match[0].trim();
      break;
    }
  }

  // Generic location fallback
  if (!location) {
    const locMatch = text.match(/(?:location|based in|located in)[:\s]+([A-Z][a-z]+(?:[,\s]+[A-Z]{2})?)/i);
    if (locMatch) location = locMatch[1];
  }

  // Only include if it's SF or Remote
  if (location) {
    const locationLower = location.toLowerCase();
    if (!locationLower.includes('san francisco') && 
        !locationLower.includes('bay area') && 
        !locationLower.includes('remote') &&
        !locationLower.includes('sf')) {
      continue; // Skip non-SF/remote jobs
    }
  }

  // Extract URLs
  let jobUrl = '';
  const urls = text.match(/https:\/\/[^\s]+/g);
  if (urls && urls.length > 0) {
    // Prefer job board URLs
    jobUrl = urls.find(url => 
      url.includes('greenhouse') || 
      url.includes('lever') || 
      url.includes('workday') ||
      url.includes('jobs') ||
      url.includes('careers')
    ) || urls[0];
  }

  // Build tweet URL
  const tweetUrl = `https://twitter.com/${author.username}/status/${tweet.id}`;

  // Extract poster info
  const posterName = author.name || 'Unknown';
  const posterUsername = author.username || '';
  const posterProfile = `https://twitter.com/${author.username}`;
  const posterBio = author.description || '';

  // Try to extract poster's title from bio
  let posterTitle = '';
  const titleMatch = posterBio.match(/^([^@|•\n]{10,80})/);
  if (titleMatch) {
    posterTitle = titleMatch[1].trim();
  }

  results.push({
    json: {
      job_title: jobTitle || 'Senior Product Designer',
      company: company,
      location: location || 'Remote/San Francisco',
      job_url: jobUrl || tweetUrl,
      posted_date: tweet.created_at || new Date().toISOString(),
      source: 'twitter',
      poster_name: posterName,
      poster_username: posterUsername,
      poster_profile: posterProfile,
      poster_title: posterTitle,
      description: text.substring(0, 2000),
      tweet_url: tweetUrl,
      likes: tweet.public_metrics?.like_count || 0,
      retweets: tweet.public_metrics?.retweet_count || 0,
      replies: tweet.public_metrics?.reply_count || 0
    }
  });
}

return results;
