      "painSeverity": "low/medium/high/critical"
    },
    "timeline": {
      "score": 0-25,
      "assessment": "description",
      "urgency": "immediate/1-3months/3-6months/6+months"
    }
  },
  "keyInsights": ["insight1", "insight2"],
  "redFlags": ["flag1", "flag2"],
  "opportunitySize": "small/medium/large/enterprise",
  "competitorVulnerability": "low/medium/high",
  "recommendedAction": "immediate call/email nurture/demo/disqualify",
  "idealSalesRep": "senior/mid-level/junior/SDR",
  "conversionProbability": "0-100%",
  "estimatedDealValue": "dollar amount or range",
  "nextSteps": ["step1", "step2", "step3"],
  "talkingPoints": ["point1", "point2", "point3"]
}

