const salesTeam = {
  senior: [
    {
      name: "Sarah Johnson",
      email: "sarah.johnson@company.com",
      territory: ["North America", "Europe"],
      industries: ["Technology", "Software", "SaaS"],
      minDealSize: 50000,
      currentLeads: 8
    },
    {
      name: "Michael Chen",
      email: "michael.chen@company.com",
      territory: ["Asia Pacific", "North America"],
      industries: ["Healthcare", "Finance", "Insurance"],
      minDealSize: 75000,
      currentLeads: 6
    }
  ],
  midLevel: [
    {
      name: "Jessica Martinez",
      email: "jessica.martinez@company.com",
      territory: ["North America", "Latin America"],
      industries: ["Retail", "E-commerce", "Manufacturing"],
      minDealSize: 25000,
      currentLeads: 12
    },
    {
      name: "David Williams",
      email: "david.williams@company.com",
      territory: ["Europe", "Middle East"],
      industries: ["Professional Services", "Consulting", "Education"],
      minDealSize: 20000,
      currentLeads: 10
    }
  ],
  sdr: [
    {
      name: "Emily Brown",
      email: "emily.brown@company.com",
      territory: ["All"],
      industries: ["All"],
      minDealSize: 0,
      currentLeads: 25
    },
    {
      name: "Alex Rodriguez",
      email: "alex.rodriguez@company.com",
      territory: ["All"],
      industries: ["All"],
      minDealSize: 0,
      currentLeads: 22
    }
  ]
};

const leadData = $input.first().json;
const leadScore = leadData.leadScore;
const qualificationTier = leadData.qualificationTier;
const industry = $('Extract Lead Data').first().json.industry;
const companySize = $('Extract Lead Data').first().json.companySize;

let repLevel;
if (leadScore >= 75) {
  repLevel = 'senior';
} else if (leadScore >= 50) {
  repLevel = 'midLevel';
} else {
  repLevel = 'sdr';
}

let assignedRep = null;
const eligibleReps = salesTeam[repLevel];

let matchedReps = eligibleReps.filter(rep => {
  if (repLevel === 'sdr') return true;
  return rep.industries.includes(industry) || rep.industries.includes('All');
});

if (matchedReps.length === 0) {
  matchedReps = eligibleReps;
}

assignedRep = matchedReps.reduce((prev, current) => {
  return (prev.currentLeads < current.currentLeads) ? prev : current;
});

const output = {
  assignedRepName: assignedRep.name,
  assignedRepEmail: assignedRep.email,
  repLevel: repLevel,
  assignmentReason: `Assigned to ${assignedRep.name} (${repLevel}) based on lead score ${leadScore}, industry expertise (${industry}), and current workload (${assignedRep.currentLeads} leads)`,
  routingTimestamp: new Date().toISOString(),
  ...leadData,
  ...$('Extract Lead Data').first().json
};

return { json: output };
