const input = $input.item.json;

// Extract Slack message text
const messageText = input.message?.text || "No message text found.";

// Default placeholders
let jiraKey = "Unknown";
let service = "Unknown";
let severity = "Unknown";
let description = "No description provided.";
let jiraLink = "";

// Parse from blocks
if (input.message?.blocks && Array.isArray(input.message.blocks)) {
  const elements = input.message.blocks[0]?.elements[0]?.elements || [];

  for (let i = 0; i < elements.length; i++) {
    const el = elements[i];
    const text = (el.text || "").trim();

    if (text.startsWith("SCRUM-")) jiraKey = text;
    if (text === "API") service = text;
    if (text === "High" || text === "Medium" || text === "Low") severity = text;
    if (text.includes("Response time")) description = text;
    if (el.type === "link" && el.url.includes("atlassian.net")) jiraLink = el.url;
  }
}

