const out = [];
for (const item of items) {
  const epics = item.json["Jira Epic Keys"]
    .split(',')
    .map(e => e.trim())
    .filter(Boolean);
  for (const epic of epics) {
    out.push({
      json: {
        kr_id: item.json.id,
        kr_name: item.json.name,
        objective_name: item.json["Objective Name"],
        epic_key: epic,
        target_progress: Number(item.json["Target Progress"]),
        current_progress: Number(item.json["Current Progress"]),
        threshold: Number(item.json["Threshold"]),
        owner_email: item.json["Owners Email"]
      }
    });
  }
}
return out;

const items = $input.all();

const formatted = items.flatMap(item => {
  const data = item.json;
  const issues = Array.isArray(data) ? data : [data];

  return issues.map(issue => {
    const f = issue.fields;
    return {
      json: {
        key: issue.key,
        name: f.summary,
        status: f.status.name,
        assignee: f.assignee ? f.assignee.displayName : 'Unassigned',
        dueDate: f.duedate || 'N/A',
        created: f.created,
        updated: f.updated,
        description: f.description || 'No description'
      }
    };
  });
});

return formatted;

const items = $input.all();
const grouped = {};

for (const i of items) {
  const krId = i.json.kr_id;
  if (!grouped[krId]) grouped[krId] = { meta: i.json, epics: [] };
  grouped[krId].epics.push(i.json);
}

const out = [];

for (const [krId, data] of Object.entries(grouped)) {
  const { target_progress, threshold, owner_email, kr_name } = data.meta;
  const epics = data.epics;

  const statusWeight = { "To Do": 0, "In Progress": 50, "Done": 100 };
  const progressValues = epics.map(e => statusWeight[e.epic_status] ?? 0);

  const actual_progress = progressValues.length
    ? progressValues.reduce((a, b) => a + b, 0) / progressValues.length
    : 0;

  const variance = Number((actual_progress - target_progress).toFixed(2));
  const variance_abs = Math.abs(variance);

  const status =
    variance_abs > threshold
      ? variance < 0
        ? "At Risk"
        : "Ahead"
      : "On Track";

  out.push({
    json: {
      kr_id: krId,
      kr_name,
      owner_email,
      target_progress,
      actual_progress: Number(actual_progress.toFixed(2)),
      variance,
      variance_abs,
      status,
      epic_count: epics.length,
      last_updated: new Date().toISOString()
    }
  });
}

return out;

